select rownum v_0,bb.* from
(select xm.xmmc v_1,xd.jhxdwh v_2,xm.ztz v_3,nvl(xd.ztz,0) v_4,nvl(xd.cgs,0) v_5,nvl(xd.rys,0) v_6,nvl(xd.ztz,0)-nvl(xd.cgs,0)-nvl(xd.rys,0)-nvl(xd.dfzc,0) v_7,nvl(xd.dfzc,0) v_8,
dw.sn v_9,dw.bn v_10,dw.ztz v_11,bf.sn v_12,bf.bn v_13,bf.ztz v_14,nvl(xd.ztz,0)-nvl(dw.ztz,0) v_15,nvl(dw.ztz,0)-nvl(bf.ztz,0) v_16,' ' v_17,substr(xm.xzqhdm,0,4)||'00' xzqhdm

 from xmjbxx_bb xm,jhzxqkb_xd xd,
(select xmbm,jhxdwh,sum(decode(substr(dwyf,0,4),to_char(${jhnf}-1),bb+sb+df,0)) sn,sum(decode(substr(dwyf,0,4),to_char(${jhnf}),bb+sb+df,0)) bn,sum(bb+sb+df) ztz  from tjbb_dw group by xmbm,jhxdwh) dw,
(select xmbm,jhxdwh,sum(decode(substr(bfyf,0,4),to_char(${jhnf}-1),bb+sb+df,0)) sn,sum(decode(substr(bfyf,0,4),to_char(${jhnf}),bb+sb+df,0)) bn,sum(bb+sb+df) ztz  from tjbb_bf group by xmbm,jhxdwh) bf
where xm.xmbm=xd.xmid and xd.xmid=dw.xmbm(+) and xd.xmid=bf.xmbm(+) and xd.jhxdwh=dw.jhxdwh(+) and xd.jhxdwh=bf.jhxdwh(+)
	${xzqhdm}
	${jhxdwh}
	and xm.gydwdm like #{gydw}||'%'
	and xm.xmmc like '%'||#{xmmc}||'%'
	${xmlx}
	${xmnf}
order by xzqhdm,xd.xdnf,xd.jhxdwh)bb

select rownum v_0,bb.* from
(select xm.xmmc v_1,xd.jhxdwh v_2,xm.ztz v_3,nvl(xd.ztz,0) v_4,nvl(xd.cgs,0) v_5,nvl(xd.rys,0) v_6,nvl(xd.ztz,0)-nvl(xd.cgs,0)-nvl(xd.rys,0)-nvl(xd.dfzc,0) v_7,nvl(xd.dfzc,0) v_8,
dw.sn v_9,dw.bn v_10,dw.ztz v_11,bf.sn v_12,bf.bn v_13,bf.ztz v_14,nvl(xd.ztz,0)-nvl(dw.ztz,0) v_15,nvl(dw.ztz,0)-nvl(bf.ztz,0) v_16,' ' v_17,substr(xm.xzqhdm,0,4)||'00' xzqhdm

 from xmjbxx_bb_zrc xm,jhzxqkb_xd_zrc xd,
(select xmbm,jhxdwh,sum(decode(substr(dwyf,0,4),to_char(${jhnf}-1),bb+sb+df,0)) sn,sum(decode(substr(dwyf,0,4),to_char(${jhnf}),bb+sb+df,0)) bn,sum(bb+sb+df) ztz  from tjbb_dw_zrc group by xmbm,jhxdwh) dw,
(select xmbm,jhxdwh,sum(decode(substr(bfyf,0,4),to_char(${jhnf}-1),bb+sb+df,0)) sn,sum(decode(substr(bfyf,0,4),to_char(${jhnf}),bb+sb+df,0)) bn,sum(bb+sb+df) ztz  from tjbb_bf_zrc group by xmbm,jhxdwh) bf
where xm.xmbm=xd.xmid and xd.xmid=dw.xmbm(+) and xd.xmid=bf.xmbm(+) and xd.jhxdwh=dw.jhxdwh(+) and xd.jhxdwh=bf.jhxdwh(+)
	${xzqhdm}
	${jhxdwh}
	and xm.gydwdm like #{gydw}||'%'
	and xm.xmmc like '%'||#{xmmc}||'%'
	${xmlx}
	${xmnf}
order by xzqhdm,xd.xdnf,xd.jhxdwh)bb

	select * from(select rownum num ,t1.* from(select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx_zb t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw group by xmbm) dw on t.xmbm=dw.xmids
	where 1=1 
	${xzqh}
	and t.gydwdm like #{gydw}||'%'
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	) t1 where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}
	and rownum <=(#{page} * #{rows}) 
	order by decode(xsbzt,null,0,xsbzt) desc,decode(ssbzt,null,0,ssbzt) desc,decode(shzt,null,0,shzt) desc,xmnf
	)t2
	where t2.num>(#{page}-1)*#{rows}

	select count(*) from(select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx_zb t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw group by xmbm) dw on t.xmbm=dw.xmids
	where 1=1 
	${xzqh}
	and t.gydwdm like #{gydw}||'%'
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

select distinct dwyf v_0 from xm_zjdw where 
1=1
and xmbm=#{xmbm} 
and substr(dwyf,0,4)=#{jhnf}
 order by v_0

select distinct bfyf v_0 from xm_zjbf where 
1=1
and xmbm=#{xmbm} 
and substr(bfyf,0,4)=#{jhnf}
 order by v_0

select v_0,v_1,v_2,rownum v_3,to_char(v_4,'99990.999') v_4,to_char(v_5,'99990.999') v_5, to_char(v_6,'99990.999') v_6,to_char(v_7,'99990.999') v_7,to_char(v_8,'99990.9999') v_8, to_char(v_9,'99990.9999') v_9,to_char(v_10,'99990.9999') v_10,to_char(v_11,'99990.9999') v_11 from ( select '合计' v_0,jsdj v_1,'公里' v_2,lcqcs v_4, (case when lcqms-lcqcs>0 then lcqms-lcqcs else 0 end) v_5, (case when lcqcs-lcqms>0 then lcqcs-lcqms else 0 end) v_6, lcqms v_7,zcqcs v_8,zcqms v_9,fzqcs v_10,fzqms v_11,1 px
from ( select '合计'jsdj,sum(decode(nf,'2016',lc,(select sum(lc) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) lcqcs, sum(lc) lcqms, sum(decode(nf,'2016',zcqcs,(select sum(zcqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) zcqcs, sum(zcqms) zcqms, sum(decode(nf,'2016',fzqcs,(select sum(fzqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) fzqcs, sum(fzqms) fzqms, 1 px from zcgl z where nf=#{nf}
${gydw}
)
union all
select '等级公路' v_0,jsdj v_1,'公里' v_2,lcqcs v_4, (case when lcqms-lcqcs>0 then lcqms-lcqcs else 0 end) v_5, (case when lcqcs-lcqms>0 then lcqcs-lcqms else 0 end) v_6, lcqms v_7,zcqcs v_8,zcqms v_9,fzqcs v_10,fzqms v_11,2 px
from ( select '总计'jsdj,sum(decode(nf,'2016',lc,(select sum(lc) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) lcqcs, sum(lc) lcqms, sum(decode(nf,'2016',zcqcs,(select sum(zcqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) zcqcs, sum(zcqms) zcqms, sum(decode(nf,'2016',fzqcs,(select sum(fzqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) fzqcs, sum(fzqms) fzqms, 2 px from zcgl z where nf=#{nf} and (jsdj='高速公路' or jsdj='一级公路' or jsdj='二级公路' or jsdj='三级公路' or jsdj='四级公路')
${gydw}
)
union all
select decode(jsdj,'等外公路','等外公路','等级公路') v_0,jsdj v_1,'公里' v_2,lcqcs v_4, (case when lcqms-lcqcs>0 then lcqms-lcqcs else 0 end) v_5, (case when lcqcs-lcqms>0 then lcqcs-lcqms else 0 end) v_6, lcqms v_7,zcqcs v_8,zcqms v_9,fzqcs v_10,fzqms v_11,px
from ( select jsdj,sum(decode(nf,'2016',lc,(select sum(lc) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) lcqcs, sum(lc) lcqms, sum(decode(nf,'2016',zcqcs,(select sum(zcqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) zcqcs, sum(zcqms) zcqms, sum(decode(nf,'2016',fzqcs,(select sum(fzqms) from zcgl z1 where nf=to_char(${nf}-1) and z1.jsdj=z.jsdj ))) fzqcs, sum(fzqms) fzqms, min(decode(jsdj,'高速公路',3,'一级公路',4,'二级公路',5,'三级公路',6,'四级公路',7,'等外公路',8)) px from zcgl z where nf=#{nf}
${gydw}
group by jsdj )
order by px ) t

SELECT 
XH,MIN(V_0) V_0,FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_1),'1',0,V_1)),2) V_1,DECODE(MAX(V_2),'——','——',FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_2),'1',0,V_2)),2)) V_2		
FROM YY2CSBB WHERE 1=1 AND XMLX=#{xmlx}
	${gydw}
	AND NF = #{nf}
	${yf}
	
GROUP BY XH
ORDER BY XH

		SELECT 
		XH,MIN(V_0) V_0,
		FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_1),'1',0,V_1)),2) V_1,
    	MIN(V_2) V_2,
		FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_3),'1',0,V_3)),2) V_3
    	FROM YY2CSBB WHERE 1=1 AND XMLX=#{xmlx}
		${gydw}
		AND NF = #{nf}
		${yf}
			
		GROUP BY XH
		ORDER BY XH

		SELECT 
		XH,MIN(V_0) V_0,
		FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_1),'1',0,V_1)),2) V_1,
    	FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_2),'1',0,V_2)),2) V_2
    	FROM YY2CSBB WHERE 1=1 AND XMLX=#{xmlx}
		${gydw}
		AND NF = #{nf}
		${yf}
			
		GROUP BY XH
		ORDER BY XH

		SELECT XH,
       	MIN(V_0) V_0,
       	FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_1), '1', 0, V_1)),2) V_1,
       	FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_2), '1', 0, V_2)),2) V_2,      
       	MIN(V_3) V_3,
       	FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_4), '1', 0, V_4)),2) V_4,
       	FUN_CHAR(SUM(DECODE(F_STR_OR_NUM(V_5), '1', 0, V_5)),2) V_5
  		FROM YY2CSBB WHERE 1=1 AND XMLX=#{xmlx}
		${gydw}
		AND NF = #{nf}
		${yf}
		GROUP BY XH
		ORDER BY XH

