    nvl(dndw.sb,0) v_11,
    nvl(dnbf.sb,0) v_12,
     jy.snsjz+nvl(dndw.sb,0)-nvl(dnbf.sb,0) v_13,jy.yqnsjz v_14, jy.dssnsjz v_15, jy.dsnsjz v_16,jy.nsjz v_17,
    nvl(tj.sb,0) v_18,'' v_19,decode(x.jsxz,'养护大中修',x.jsxz,x.gcfl) xmlx
    from xmjbxx_bb x,(
