    nvl(dnjh.dnbb,0)+nvl(dnjh.dnsb,0) v_5,
    nvl(yqjh.yqbb,0)+nvl(yqjh.yqsb,0)+nvl(dnjh.dnbb,0)+nvl(dnjh.dnsb,0) v_6,
    jy.snbjz+jy.snsjz v_7,nvl(jy.yqnbjz1,0)+nvl(jy.yqnsjz1,0) v_8,jy.ssnbjz+jy.ssnsjz v_9,jy.snbjz+jy.snsjz v_10,
    nvl(dndw.bb,0)+nvl(dndw.sb,0) v_11,
    nvl(dnbf.bb,0)+nvl(dnbf.sb,0) v_12,
     jy.snbjz+jy.snsjz+nvl(dndw.bb,0)+nvl(dndw.sb,0)-nvl(dnbf.bb,0)-nvl(dnbf.sb,0) v_13,jy.yqnbjz+jy.yqnsjz v_14, jy.dssnbjz+jy.dssnsjz v_15, jy.dsnbjz+jy.dsnsjz v_16,jy.nbjz+jy.nsjz v_17,
    nvl(tj.bb,0)+nvl(tj.sb,0) v_18,'' v_19,decode(x.jsxz,'养护大中修',x.jsxz,x.gcfl) xmlx
    from xmjbxx_bb x,(
    select * from TJBB_YQJH where nf=#{nf}
	) yqjh,(
	select * from TJBB_DNJH where nf=#{nf}
	) dnjh ,(
	select * from TJBB_DNDW where ny=#{nf}||'-'||#{yf}
	) dndw,(
	select * from TJBB_DNBF where ny=#{nf}||'-'||#{yf}
	) dnbf,(
	select * from XMJY_LSB where nf=#{nf}
	) jy,(
     select * from tjbb_tj
    )tj where x.xmbm=yqjh.xmid(+) and x.xmbm=dnjh.xmid(+) and x.xmbm=dndw.xmbm(+) 
     and x.xmbm=jy.xmbm(+) and x.xmbm=dnbf.xmbm(+) and x.xmbm=tj.xmbm(+)
    
    ${gydw}
     
    
    ${xmnf}
     
   	
    ${xmlx}
    
    )
    group by xmlx
    
    order by xmlx
