mapper.update:
update xtgl_master set truename=#{truename}

	 ,name =#{name}


	 ,password=#{password} 


	 ,sex=#{sex} 


	 ,tel=#{tel} 


	 ,phone=#{phone} 


	 ,idcard=#{idcard} 


	 ,roleid=#{roleid}


	 ,unit=#{unit} 

where id=#{id}

mapper.select:
