mapper.select:
	select * from(select rownum num ,t1.*  from (select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr from xm_zjbf where to_number(sbthcd)<=${sbthcd} group by xmbm
    ) dw on t.xmbm=dw.xmids
    left join (select xmb.xmbm sfxmbm,decode(bfbz,null,'否',dwbz,'是','否') sfqbbf from 
	xmjbxx xmb left join (select xmbm,sum(ztz)-sum(dfzc) bfbz from xm_zjbf where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) bfb on xmb.xmbm=bfb.xmbm  left join (select xmbm,sum(ztz)-sum(dfzc) dwbz from xm_zjdw where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) dwb on bfb.xmbm=dwb.xmbm ) sf on t.xmbm=sf.sfxmbm
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	${sfqbbf}
	) t1 where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}
	and rownum <=(#{page} * #{rows}) 
	order by decode(xsbzt,null,0,xsbzt) desc,decode(ssbzt,null,0,ssbzt) desc,decode(shzt,null,0,shzt) desc,xmnf

	)t2
	where t2.num>(#{page}-1)*#{rows}

	select count(*) from (select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr from xm_zjbf  where to_number(sbthcd)<=${sbthcd} group by xmbm
    ) dw on t.xmbm=dw.xmids
	left join (select xmb.xmbm sfxmbm,decode(bfbz,null,'否',dwbz,'是','否') sfqbbf from 
	xmjbxx xmb left join (select xmbm,sum(ztz)-sum(dfzc) bfbz from xm_zjbf where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) bfb on xmb.xmbm=bfb.xmbm  left join (select xmbm,sum(ztz)-sum(dfzc) dwbz from xm_zjdw where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) dwb on bfb.xmbm=dwb.xmbm ) sf on t.xmbm=sf.sfxmbm
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	${sfqbbf}
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

	select count(*) xmsl,round(sum(nvl(dwzj,0)),2) dwzj,sum(nvl(bfzj,0)) bfzj,sum(nvl(yshbfzj,0)) yshbfzj,sum(nvl(jhxdzj,0)) jhxdzj from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
    left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
    on t.xmbm=xd.xmid 
    left join(
      select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
    (case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
    (case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
    (case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
    from xm_zjbf  where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
    left join
      (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(bfzj,0) bfzj,nvl(yshbfzj,0) yshbfzj,nvl(jhxdzj,0) jhxdzj from xmjbxx xm left join 
      (select xmbm,sum(ztz) dwzj from xm_zjdw  where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
		  (select xmbm,sum(ztz) bfzj,sum(decode(shzt,'已审核',ztz,0)) yshbfzj from xm_zjbf  where to_number(sbthcd)<=${sbthcd} group by xmbm) bf on xm.xmbm=bf.xmbm left join
		  (select xmid xmbm,sum(nvl(ztz,0)) jhxdzj from xm_gsd_jhxd group by xmid) xd on xm.xmbm=xd.xmbm 
    ) zj on t.xmbm=zj.xmbms1
    left join (select xmb.xmbm sfxmbm,decode(bfbz,null,'否',dwbz,'是','否') sfqbbf from 
	xmjbxx xmb left join (select xmbm,sum(ztz)-sum(dfzc) bfbz from xm_zjbf where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) bfb on xmb.xmbm=bfb.xmbm  left join (select xmbm,sum(ztz)-sum(dfzc) dwbz from xm_zjdw where to_number(sbthcd)<=${sbthcd} and (case when ${sbthcd}=7 then shzt else '已审核' end)='已审核' group by xmbm) dwb on bfb.xmbm=dwb.xmbm ) sf on t.xmbm=sf.sfxmbm
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	${sfqbbf}
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

select * from(select rownum num ,t.*  from xm_zjbf t
where 1=1 and xmbm=#{xmbm} and to_number(sbthcd)<=to_number(#{sbthcd})
and rownum <=(#{page} * #{rows}) 
order by xsbzt,ssbzt,shzt,bfyf
)t2
where t2.num>(#{page}-1)*#{rows}

mapper.update:
update xm_zjbf
xmbm = #{xmbm}, ztz = #{ztz}, cgs = #{cgs}, gz = #{gz}, sz = #{sz}, zq = #{zq}, tdk = #{tdk}, jl = #{jl}, qt = #{qt}, rys = #{rys}, dfzc = #{dfzc}, yhdk = #{yhdk}, stz = #{stz}, jhxdwh = #{jhxdwh}, bd = #{bd}, sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth}, bfyf = #{bfyf}, ttc = #{ttc}, tbr = #{tbr}, tbsj = #{tbsj}, bz = #{bz},
where 1=1 and ${id}

update xm_zjbf
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${id}

update xm_zjbf
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${xmbm}

mapper.select:
select * from(select rownum num ,t.*,(select name from xtgl_department where id=t.gydwdm) gydw  from xm_zjbf t
where 1=1 and gydwdm is not null 
${gydw}
and bfyf like '%'||#{bfyf}||'%'
and rownum <=(#{page} * #{rows}) 
order by nf,gydwdm
) t1 
)t2
where t2.num>(#{page}-1)*#{rows}

mapper.update:
update xm_zjbf
ztz = #{ztz}, cgs = #{cgs}, rys = #{rys}, ttc = #{ttc}, dfzc = #{dfzc}, bd = #{bd}, bfyf = #{bfyf}, jhxdwh = #{jhxdwh}, parent = #{parent}, gydwdm = #{gydwdm}, xmbm = #{xmbm}, bz = #{bz}, nf=#{nf}, tbr=#{tbr}, tbsj=#{tbsj},
where 1=1 and gydwdm=#{gydwdm} and bfyf=#{bfyf} and nf=#{nf} and jhxdwh=#{jhxdwh}

mapper.select:
 select 
  t1.xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm}) and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjbf z where 1=1 and (z.parent=#{gydwdm}) and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmbm=t2.xmbm order by gydwdm

 select 
  t1.xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjbf z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmbm=t2.xmbm order by gydwdm

		select xmbm,xmnf,xmmc,gydw,xzqh,ztz jhztz,dwcgs dwcgs,dwrys dwrys,dwdfzc dwdfzc,dwttc dwttc,jhxdwh,'没有标段' bd,
    	'0' cgs,'0' rys,'0' dfzc,'0' ttc,to_char(sysdate,'yyyy-mm') bfyf,gydw tbr,to_char(sysdate,'yyyy-mm-dd') tbsj,'' bz 
    	 from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	    left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	    on t.xmbm=xd.xmid 
	    left join(
	      select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	    (case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	    (case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	    (case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	    from xm_zjbf  where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	    left join
	      (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(bfzj,0) bfzj,nvl(yshbfzj,0) yshbfzj,nvl(jhxdzj,0) jhxdzj,nvl(dwcgs,0) dwcgs,nvl(dwrys,0) dwrys,nvl(dwttc,0) dwttc,nvl(dwdfzc,0) dwdfzc from xmjbxx xm left join 
	      (select xmbm,sum(nvl(ztz,0)) dwzj,sum(nvl(cgs,0)) dwcgs,sum(nvl(ztz,0))-sum(nvl(cgs,0))-sum(nvl(rys,0))-sum(nvl(dfzc,0)) dwttc,sum(nvl(rys,0)) dwrys,sum(nvl(dfzc,0)) dwdfzc from xm_zjdw  where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
			  (select xmbm,sum(ztz) bfzj,sum(decode(shzt,'已审核',ztz,0)) yshbfzj from xm_zjbf  where to_number(sbthcd)<=${sbthcd} group by xmbm) bf on xm.xmbm=bf.xmbm left join
			  (select xmid xmbm,sum(nvl(ztz,0)) jhxdzj from xm_gsd_jhxd where f_str_or_num(ztz)!=1 group by xmid) xd on xm.xmbm=xd.xmbm 
	    ) zj on t.xmbm=zj.xmbms1
	    where 1=1 
		${xzqh}
		${gydwdm}
		${xmnf}
		${jsxz}
		${jhxdwh}
		${gcfl}
		and xmbm like '%'||#{xmbm}||'%'
		and xmmc like '%'||#{xmmc}||'%'
		and gydwdm like #{gydw}||'%'
		) where 1=1
		${shzt}
		${ssbzt}
		${xsbzt}

mapper.update:
update xm_zjbf
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and xsbzt='未上报' and sbthcd='11' ${xmbm}

update xm_zjbf
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ssbzt='未上报' and sbthcd='9' ${xmbm}
