mapper.select:
	select * from(select rownum num ,t1.* from (select t.*,xd.jhxdwh,dw.*  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt from xm_zjbf group by xmbm
    ) dw on t.xmbm=dw.xmids
	where 1=1 
	${xzqh}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	order by decode(xsbzt,null,0,xsbzt) desc,decode(ssbzt,null,0,ssbzt) desc,decode(shzt,null,0,shzt) desc,xmnf
	) t1 
	)t2
	where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

select count(*) from xmjbxx t
left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
on t.xmbm=xd.xmid	
where 1=1 
${xzqh}
${xmnf}
${jsxz}
${gcfl}
${jhxdwh}
and xmbm like '%'||#{xmbm}||'%'
and xmmc like '%'||#{xmmc}||'%'

select * from(select rownum num ,t1.* from (select t.*  from xm_zjbf t
where 1=1 and xmbm=#{xmbm} and to_number(sbthcd)<=to_number(#{sbthcd})
order by xsbzt,ssbzt,shzt,bfyf
) t1 
)t2
where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

mapper.update:
update xm_zjbf
xmbm = #{xmbm}, ztz = #{ztz}, cgs = #{cgs}, gz = #{gz}, sz = #{sz}, zq = #{zq}, tdk = #{tdk}, jl = #{jl}, qt = #{qt}, rys = #{rys}, dfzc = #{dfzc}, yhdk = #{yhdk}, stz = #{stz}, jhxdwh = #{jhxdwh}, bd = #{bd}, sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth}, bfyf = #{bfyf},
where 1=1 and ${id}

update xm_zjbf
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${id}

mapper.select:
select * from(select rownum num ,t1.* from (select t.*,(select name from xtgl_department where id=t.gydwdm) gydw  from xm_zjbf t
where 1=1 and gydwdm is not null 
${gydw}
${nf}
order by nf,gydwdm
) t1 
)t2
where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

select count(*) from xm_zjbf t
where 1=1 and gydwdm is not null 
${gydw}
${nf}

mapper.update:
update xm_zjbf
ztz = #{ztz}, cgs = #{cgs}, rys = #{rys}, ttc = #{ttc}, dfzc = #{dfzc}, bd = #{bd}, nf = #{nf}, jhxdwh = #{jhxdwh}, parent = #{parent}, gydwdm = #{gydwdm},
where 1=1 and gydwdm=#{gydwdm} and nf=#{nf}
