mapper.select:
	select * from(select rownum num ,t1.* from (select * from(select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}
	order by decode(xsbzt,null,0,xsbzt) desc,decode(ssbzt,null,0,ssbzt) desc,decode(shzt,null,0,shzt) desc,xmnf
	) t1
	
	)t2
	where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

	select count(*) from(select t.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

	select count(*) xmsl,sum(nvl(dwzj,0)) dwzj,sum(nvl(yshdwzj,0)) yshdwzj,sum(nvl(jhxdzj,0)) jhxdzj from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	left join
    (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(yshdwzj,0) yshdwzj,nvl(jhxdzj,0) jhxdzj,nvl(jhxdcgs,0) jhxdcgs,nvl(jhxdstz,0) jhxdstz from xmjbxx xm left join 
		(select xmbm,sum(ztz) dwzj,sum(decode(shzt,'已审核',ztz,0)) yshdwzj from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
		(select xmid xmbm,sum(nvl(ztz,0)) jhxdzj,sum(nvl(btzzj,0)) jhxdcgs,sum(nvl(ztz,0))-sum(nvl(btzzj,0))-sum(nvl(dfzc,0)) jhxdstz from xm_gsd_jhxd group by xmid) xd on xm.xmbm=xd.xmbm 
    ) zj on t.xmbm=zj.xmbms1
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

select * from(select rownum num ,t1.* from (select t.*,(select name from xtgl_department where id=t.gydwdm) gydw   from xm_zjdw t
where 1=1 and gydwdm is not null 
${gydw}
and dwyf like '%'||#{dwyf}||'%'
order by nf,gydwdm
) t1 
)t2
where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

select * from(select rownum num ,t1.* from (select t.*  from xm_zjdw t
where 1=1 and xmbm=#{xmbm} and to_number(sbthcd)<=to_number(#{sbthcd})
order by xsbzt,ssbzt,shzt,dwyf
) t1 
)t2
where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

select * from(select rownum num ,t1.* from (select t.*  from XM_GSD_JHXD t
where 1=1 and xmid=#{xmbm} 
order by xdnf
) t1 
)t2
where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

mapper.update:
update xm_zjdw
xmbm = #{xmbm}, ztz = #{ztz}, cgs = #{cgs}, gz = #{gz}, sz = #{sz}, zq = #{zq}, tdk = #{tdk}, jl = #{jl}, qt = #{qt}, rys = #{rys}, dfzc = #{dfzc}, yhdk = #{yhdk}, stz = #{stz}, jhxdwh = #{jhxdwh}, bd = #{bd}, sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth}, dwyf = #{dwyf}, ttc = #{ttc}, tbr = #{tbr}, tbsj = #{tbsj},
where 1=1 and ${id}

update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${id}

update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${xmbm}

update xm_zjdw
ztz = #{ztz}, cgs = #{cgs}, rys = #{rys}, ttc = #{ttc}, dfzc = #{dfzc}, bd = #{bd}, dwyf = #{dwyf}, jhxdwh = #{jhxdwh}, parent = #{parent}, gydwdm = #{gydwdm}, xmbm = #{xmbm}, nf=#{nf}, tbr=#{tbr}, tbsj=#{tbsj},
where 1=1 and gydwdm=#{gydwdm} and dwyf=#{dwyf} and nf=#{nf} and jhxdwh=#{jhxdwh}

mapper.select:
 select 
  xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from (
  select xmid,gydwdm,sum(nvl(btzzj,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(dfzc,0)) dfzc,sum(nvl(ttc,0)) ttc from xm_gsd_jhxd z where 1=1 and (z.parent=#{gydwdm}) and substr(xmid,0,4)=#{nf} and jhxdwh=#{jhxdwh} group by xmid,gydwdm order by gydwdm 
  ) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm}) and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmid=t2.xmbm order by gydwdm

 select 
  xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from (
  select xmid,gydwdm,sum(nvl(btzzj,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(dfzc,0)) dfzc,sum(nvl(ttc,0)) ttc from xm_gsd_jhxd z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and substr(xmid,0,4)=#{nf} and jhxdwh=#{jhxdwh} group by xmid,gydwdm order by gydwdm 
  ) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmid=t2.xmbm order by gydwdm

	select xmbm,xmnf,xmmc,gydw,xzqh,ztz jhztz,jhxdcgs xdcgs,jhxdrys xdrys,jhxddfzc xddfzc,jhxdzj-jhxdcgs-jhxdrys-jhxddfzc xdttc,jhxdwh,
	'0' cgs,'0' rys,'0' dfzc,'0' ttc,to_char(sysdate,'yyyy-mm') dwyf,gydw tbr,to_char(sysdate,'yyyy-mm-dd') tbsj
	from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	left join
    (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(yshdwzj,0) yshdwzj,nvl(jhxdzj,0) jhxdzj,nvl(jhxdcgs,0) jhxdcgs,nvl(jhxdstz,0) jhxdstz,nvl(jhxddfzc,0) jhxddfzc,nvl(jhxdrys,0) jhxdrys from xmjbxx xm left join 
		(select xmbm,sum(ztz) dwzj,sum(decode(shzt,'已审核',ztz,0)) yshdwzj from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
		(select xmid xmbm,sum(nvl(ztz,0)) jhxdzj,sum(nvl(btzzj,0)) jhxdcgs,sum(nvl(ztz,0))-sum(nvl(btzzj,0))-sum(nvl(dfzc,0)) jhxdstz,sum(nvl(dfzc,0)) jhxddfzc,sum(nvl(rys,0)) jhxdrys from xm_gsd_jhxd group by xmid) xd on xm.xmbm=xd.xmbm 
    ) zj on t.xmbm=zj.xmbms1
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	${jsxz}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

