mapper.select:
		select * from(select rownum num ,t1.* from(select t.*,xd.jhxdwh,decode(dw.dwbz,null,'否',xd.xdbz,'是','否') sfqbdw,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx_zb t
		left join
    	(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh,sum(nvl(btzzj,0))+sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(rys,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0)) xdbz from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
		on t.xmbm=xd.xmid	
		left join(
	    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
		(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
		(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
		(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr,
    	sum(decode(${sbthcd},7,decode(shzt,'已审核',ztz-dfzc-yhdk,0),ztz-dfzc)) dwbz
		from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
		where 1=1 
		${xzqh}
		${gydwdm}
		${xmnf}
		and jsxz =#{jsxz}
		${knw}
		${jhxdwh}
		${gcfl}
		and xmbm like '%'||#{xmbm}||'%'
		and xmmc like '%'||#{xmmc}||'%'
		and gydwdm like #{gydw}||'%'
		
		) t1 where 1=1
		${shzt}
		${ssbzt}
		${xsbzt}
		${sfqbdw}
		and rownum <=(#{page} * #{rows})  
		order by decode(xsbzt,null,0,xsbzt) desc,decode(ssbzt,null,0,ssbzt) desc,decode(shzt,null,0,shzt) desc,xmnf
		)t2
		where t2.num>(#{page}-1)*#{rows}

		select count(*) from(select t.*,xd.jhxdwh,decode(dw.dwbz,null,'否',xd.xdbz,'是','否') sfqbdw,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx_zb t
		left join
    	(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh,sum(nvl(btzzj,0))+sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(rys,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0)) xdbz from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
		on t.xmbm=xd.xmid	
		left join(
	    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
		(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
		(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
		(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr,
    	sum(decode(${sbthcd},7,decode(shzt,'已审核',ztz-dfzc-yhdk,0),ztz-dfzc)) dwbz
		from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
		where 1=1 
		${xzqh}
		${gydwdm}
		${xmnf}
		and jsxz =#{jsxz}
		${knw}
		${jhxdwh}
		${gcfl}
		and xmbm like '%'||#{xmbm}||'%'
		and xmmc like '%'||#{xmmc}||'%'
		and gydwdm like #{gydw}||'%'
		
		) where 1=1
		${sfqbdw}
		${shzt}
		${ssbzt}
		${xsbzt}

		select count(*) xmsl,sum(nvl(dwzbz,0)) dwzbz,sum(nvl(dwbb,0)) dwbb,sum(nvl(dwsb,0)) dwsb,sum(nvl(dwdf,0)) dwdf,sum(nvl(bfzbz,0)) bfzbz,sum(nvl(bfbb,0)) bfbb,sum(nvl(bfsb,0)) bfsb,sum(nvl(bfdf,0)) bfdf,sum(nvl(tjzbz,0)) tjzbz,sum(nvl(tjbb,0)) tjbb,sum(nvl(tjsb,0)) tjsb,sum(nvl(tjdf,0)) tjdf,sum(nvl(xdzbz,0)) xdzbz,sum(nvl(xdbb,0)) xdbb,sum(nvl(xdsb,0)) xdsb,sum(nvl(xddf,0)) xddf from(
        select t.*,xd.jhxdwh,decode(dw.dwzbz,null,'否',xd.xdzbz,'是','否') sfqbdw,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr,nvl(dwzbz,0) dwzbz,nvl(dwbb,0) dwbb,nvl(dwsb,0) dwsb,nvl(dwdf,0) dwdf,nvl(bfzbz,0) bfzbz,nvl(bfbb,0) bfbb,nvl(bfsb,0) bfsb,nvl(bfdf,0) bfdf,nvl(tjzbz,0) tjzbz,nvl(tjbb,0) tjbb,nvl(tjsb,0) tjsb,nvl(tjdf,0) tjdf,nvl(xdzbz,0) xdzbz,nvl(xdbb,0) xdbb,nvl(xdsb,0) xdsb,nvl(xddf,0) xddf from xmjbxx_zb t
        left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh,sum(nvl(btzzj,0))+sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(rys,0))+sum(nvl(stz,0)+ nvl(ttc, 0) + nvl(zddzjl, 0)) xdzbz,sum(nvl(btzzj,0)) xdbb,sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(rys,0))+sum(nvl(stz,0)+ nvl(ttc, 0) + nvl(zddzjl, 0)) xdsb,sum(nvl(yhdk,0))+sum(nvl(dfzc,0)) xddf from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
        on t.xmbm=xd.xmid 
        left join(
        select xmbm xmids,sum(ztz) bfzj,sum(nvl(cgs, 0)) bfbb,sum(nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) + nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) bfsb,sum(nvl(cgs, 0) + nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) +nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) bfzbz,sum(nvl(dfzc,0)+nvl(yhdk,0)) bfdf 
        from xm_zjbf where to_number(sbthcd)<=${sbthcd} group by xmbm
        ) bf on t.xmbm=bf.xmids
        left join 
        (select xmbm xmidss,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
        (case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
        (case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
        (case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr,sum(ztz) dwzj,sum(nvl(cgs, 0)) dwbb,sum(nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) + nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) dwsb,sum(nvl(cgs, 0) + nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) +nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) dwzbz,sum(nvl(dfzc,0)+nvl(yhdk,0)) dwdf
        from xm_zjdw where to_number(sbthcd)<=${sbthcd} and trxmbm is null group by xmbm) dw on t.xmbm=dw.xmidss
        left join (select sum(nvl(cgs, 0)) tjbb,sum(nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) + nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) tjsb,sum(nvl(cgs, 0) + nvl(gz, 0) +nvl(sz, 0) + nvl(zq, 0) + nvl(tdk, 0) + nvl(jl, 0) +nvl(qt, 0) + nvl(rys, 0) + nvl(stz, 0) + nvl(ttc, 0) +nvl(zddzjl, 0)) tjzbz,sum(nvl(dfzc,0)+nvl(yhdk,0)) tjdf,xmbm xmbmsss from xm_zjtj group by xmbm) tj on t.xmbm=tj.xmbmsss
        where 1=1 
		${xzqh}
		${gydwdm}
		${xmnf}
		and jsxz =#{jsxz}
		${knw}
		${jhxdwh}
		${gcfl}
		and xmbm like '%'||#{xmbm}||'%'
		and xmmc like '%'||#{xmmc}||'%'
		and gydwdm like #{gydw}||'%'
		
		) where 1=1
		${sfqbdw}
		${shzt}
		${ssbzt}
		${xsbzt}

select * from
(select rownum num,t.*,(select name from xtgl_department where id=t.gydwdm) gydw   from xm_zjdw t
where 1=1 and gydwdm is not null 
${gydw}
and dwyf like '%'||#{dwyf}
${nf}
and rownum <=(#{page} * #{rows}) 
order by nf,gydwdm
)t2
where t2.num>(#{page}-1)*#{rows}

select count(*) from xm_zjdw t
where 1=1 and gydwdm is not null 
${gydw}
and dwyf like '%'||#{dwyf}
${nf}

select * from
(select rownum num ,t.*  from xm_zjdw t
where 1=1 and xmbm=#{xmbm} 
and to_number(sbthcd)<=to_number(#{sbthcd})
and rownum <=(#{page} * #{rows})
order by xsbzt,ssbzt,shzt,dwyf
)t2
where t2.num>(#{page}-1)*#{rows}

select * from
(select rownum num ,t.*  from XM_GSD_JHXD t
where 1=1 and xmid=#{xmbm} 
and rownum <=(#{page} * #{rows}) 
order by xdnf
)t2
where t2.num>(#{page}-1)*#{rows}

mapper.update:
update xm_zjdw
xmbm = #{xmbm}, ztz = #{ztz}, cgs = #{cgs}, gz = #{gz}, sz = #{sz}, zq = #{zq}, tdk = #{tdk}, jl = #{jl}, qt = #{qt}, rys = #{rys}, dfzc = #{dfzc}, yhdk = #{yhdk}, stz = #{stz}, jhxdwh = #{jhxdwh}, bd = #{bd}, sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth}, dwyf = #{dwyf}, ttc = #{ttc}, tbr = #{tbr}, tbsj = #{tbsj}, zddzjl = #{zddzjl},
where 1=1 and ${id}

update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${id}

update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ${xmbm}

update xm_zjdw
ztz = #{ztz}, cgs = #{cgs}, rys = #{rys}, ttc = #{ttc}, dfzc = #{dfzc}, bd = #{bd}, dwyf = #{dwyf}, jhxdwh = #{jhxdwh}, parent = #{parent}, gydwdm = #{gydwdm}, xmbm = #{xmbm}, nf=#{nf}, tbr=#{tbr}, tbsj=#{tbsj},
where 1=1 and gydwdm=#{gydwdm} and dwyf=#{dwyf} and nf=#{nf} and jhxdwh=#{jhxdwh}

mapper.select:
 select 
  xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from (
  select xmid,gydwdm,sum(nvl(btzzj,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(dfzc,0)) dfzc,sum(nvl(ttc,0)) ttc from xm_gsd_jhxd z where 1=1 and (z.parent=#{gydwdm}) and substr(xmid,0,4)=#{nf} and jhxdwh=#{jhxdwh} group by xmid,gydwdm order by gydwdm 
  ) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm}) and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmid=t2.xmbm order by gydwdm

 select 
  xmbm,decode(t2.gydwdm,null,t1.gydwdm,t2.gydwdm) gydwdm,nvl(t1.cgs,0)-nvl(t2.cgs,0) cgs,nvl(t1.rys,0)-nvl(t2.rys,0) rys,nvl(t1.ttc,0)-nvl(t2.ttc,0) ttc,nvl(t1.dfzc,0)-nvl(t2.dfzc,0) dfzc
  from (
  select xmid,gydwdm,sum(nvl(btzzj,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(dfzc,0)) dfzc,sum(nvl(ttc,0)) ttc from xm_gsd_jhxd z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and substr(xmid,0,4)=#{nf} and jhxdwh=#{jhxdwh} group by xmid,gydwdm order by gydwdm 
  ) t1 left join
  (
  select xmbm,gydwdm,sum(nvl(cgs,0)) cgs,sum(nvl(rys,0)) rys,sum(nvl(ttc,0)) ttc,sum(nvl(dfzc,0)) dfzc from xm_zjdw z where 1=1 and (z.parent=#{gydwdm} or gydwdm like rtrim(#{gydwdm}, '0')||'%') and nf=#{nf} and jhxdwh=#{jhxdwh} group by xmbm,gydwdm order by gydwdm 
	) t2
  on t1.xmid=t2.xmbm order by gydwdm

	select xmbm,xmnf,xmmc,gydw,xzqh,ztz jhztz,jhxdcgs xdcgs,jhxdrys xdrys,jhxddfzc xddfzc,(case when jsxz='通自然村' then jhxdstz when (jhxdzj-jhxdcgs-jhxdrys-jhxddfzc)<0 then 0 else (jhxdzj-jhxdcgs-jhxdrys-jhxddfzc) end) xdttc,jhxdwh,
	'0' cgs,'0' rys,'0' dfzc,'0' ttc,to_char(sysdate,'yyyy-mm') dwyf,gydw tbr,to_char(sysdate,'yyyy-mm-dd') tbsj
	from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr  from xmjbxx_zb t
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	left join
    (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(yshdwzj,0) yshdwzj,nvl(jhxdzj,0) jhxdzj,nvl(jhxdcgs,0) jhxdcgs,nvl(jhxdstz,0) jhxdstz,nvl(jhxddfzc,0) jhxddfzc,nvl(jhxdrys,0) jhxdrys from xmjbxx_zb xm left join 
		(select xmbm,sum(ztz) dwzj,sum(decode(shzt,'已审核',ztz,0)) yshdwzj from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
		(select xmid xmbm,sum(nvl(ztz,0)) jhxdzj,sum(nvl(btzzj,0)) jhxdcgs,sum(nvl(stz,0)) jhxdstz,sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0))+sum(nvl(ttc,0))+sum(nvl(zddzjl,0)) jhxdttc,sum(nvl(dk,0)) jhxdtdk,sum(nvl(rys,0)) jhxdrys,sum(nvl(dfzc,0)) jhxddfzc from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd on xm.xmbm=xd.xmbm 
    ) zj on t.xmbm=zj.xmbms1
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	and jsxz =#{jsxz}
	${knw}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

	select xmbm,xmnf,xmmc,gydw,xzqh,ztz jhztz,jhxdcgs xdcgs,jhxdrys xdrys,jhxddfzc xddfzc,(case when jsxz='通自然村' then jhxdstz when (jhxdzj-jhxdcgs-jhxdrys-jhxddfzc)<0 then 0 else (jhxdzj-jhxdcgs-jhxdrys-jhxddfzc) end) xdttc,nvl(jyzj,0) jyzj
	from(select t.*,zj.*,xd.jhxdwh,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr,jy.jyzj  from xmjbxx_zb t
	left join xm_jyzj jy on t.xmbm=jy.xmbm
	left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
	on t.xmbm=xd.xmid	
	left join(
    select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
	(case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
	(case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
	(case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr
	from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
	left join
    (select distinct xm.xmbm xmbms1,nvl(dwzj,0) dwzj,nvl(yshdwzj,0) yshdwzj,nvl(jhxdzj,0) jhxdzj,nvl(jhxdcgs,0) jhxdcgs,nvl(jhxdstz,0) jhxdstz,nvl(jhxddfzc,0) jhxddfzc,nvl(jhxdrys,0) jhxdrys from xmjbxx_zb xm left join 
		(select xmbm,sum(ztz) dwzj,sum(decode(shzt,'已审核',ztz,0)) yshdwzj from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on xm.xmbm=dw.xmbm left join
		(select xmid xmbm,sum(nvl(ztz,0)) jhxdzj,sum(nvl(btzzj,0)) jhxdcgs,sum(nvl(stz,0)) jhxdstz,sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0))+sum(nvl(ttc,0))+sum(nvl(zddzjl,0)) jhxdttc,sum(nvl(dk,0)) jhxdtdk,sum(nvl(rys,0)) jhxdrys,sum(nvl(dfzc,0)) jhxddfzc from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd on xm.xmbm=xd.xmbm 
    ) zj on t.xmbm=zj.xmbms1
	where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	and jsxz =#{jsxz}
	${knw}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	) where 1=1
	${shzt}
	${ssbzt}
	${xsbzt}

mapper.update:
update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and xsbzt='未上报' and sbthcd='11' ${xmbm}

update xm_zjdw
sbthcd = #{sbthcd}, thyy = #{thyy}, xsbzt = #{xsbzt},xsbsj=sysdate, ssbzt = #{ssbzt},ssbsj=sysdate, shzt = #{shzt},shsj=sysdate, sfth = #{sfth},
where 1=1 and ssbzt='未上报' and sbthcd='9' ${xmbm}

mapper.select:
select distinct tbsj from xm_zjdw where xmbm in(
	select xmbm from xmjbxx where 1=1 ${xzqh} and gydwdm like #{gydwdm}||'%'  ${jsxz}
) and xsbzt='未上报'

mapper.delete:
delete from xm_zjdw where xmbm in(
	select xmbm from xmjbxx where 1=1 ${xzqh} and gydwdm like #{gydwdm}||'%'  ${jsxz}
) and xsbzt='未上报' ${tbsj}

