	UPDATE XM_ZJDW SET xsbzt='已上报',ssbzt='已上报',shzt='已审核',shsj=sysdate
	WHERE XMBM IN 
	(
	select xmbm
	from(select t.*,xd.jhxdwh,decode(dw.dwbz,null,'否',xd.xdbz,'是','否') sfqbdw,xsbzt,ssbzt,shzt,nvl(xsbztstr,'未填写') xsbztstr,nvl(ssbztstr,'未填写') ssbztstr,nvl(shztstr,'未填写') shztstr,nvl(dwzj,0) dwzj,nvl(xdbz,0) xdbz,nvl(yshdwzj,0) yshdwzj,nvl(jhxdzj,0) jhxdzj,nvl(jhxdcgs,0) jhxdcgs,nvl(jhxdstz,0) jhxdstz,nvl(jhxdttc,0) jhxdttc,nvl(jhxdrys,0) jhxdrys,nvl(jhxdtdk,0) jhxdtdk,nvl(jhxddfzc,0) jhxddfzc  from xmjbxx_zb t
    left join(select xmid,replace(WMSYS.WM_CONCAT(distinct jhxdwh),',',',') jhxdwh,sum(nvl(btzzj,0))+sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(rys,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0)) xdbz, 
    sum(nvl(ztz,0)) jhxdzj,sum(nvl(btzzj,0)) jhxdcgs,sum(nvl(stz,0)) jhxdstz,sum(nvl(gz,0))+sum(nvl(sz,0))+sum(nvl(zq,0))+sum(nvl(dk,0))+sum(nvl(jl,0))+sum(nvl(qt,0))+sum(nvl(yhdk,0))+sum(nvl(stz,0))+sum(nvl(ttc,0))+sum(nvl(zddzjl,0)) jhxdttc,sum(nvl(dk,0)) jhxdtdk,sum(nvl(rys,0)) jhxdrys,sum(nvl(dfzc,0)) jhxddfzc
    from xm_gsd_jhxd where 1=1 ${jhxdwh} group by xmid) xd
    on t.xmbm=xd.xmid 
    left join(
      select xmbm xmids,sum(decode(xsbzt,'未上报',1,0)) xsbzt,sum(decode(ssbzt,'未上报',1,0)) ssbzt,sum(decode(shzt,'未审核',1,0)) shzt,
    (case when replace(WMSYS.WM_CONCAT(xsbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) xsbztstr,
    (case when replace(WMSYS.WM_CONCAT(ssbzt),',',',') like '%未上报%' then '未上报' else '已上报' end) ssbztstr,
    (case when replace(WMSYS.WM_CONCAT(shzt),',',',') like '%未审核%' then '未审核' else '已审核' end) shztstr,
    sum(decode(${sbthcd},7,decode(shzt,'已审核',ztz-dfzc-yhdk,0),ztz-dfzc)) dwbz,sum(ztz-dfzc) dwzj,sum(decode(shzt,'已审核',ztz-dfzc-yhdk,0)) yshdwzj
    from xm_zjdw where to_number(sbthcd)<=${sbthcd} group by xmbm) dw on t.xmbm=dw.xmids
    where 1=1 
	${xzqh}
	${gydwdm}
	${xmnf}
	and jsxz =#{jsxz}
	${knw}
	${jhxdwh}
	${gcfl}
	and xmbm like '%'||#{xmbm}||'%'
	and xmmc like '%'||#{xmmc}||'%'
	and gydwdm like #{gydw}||'%'
	
	) where 1=1
	${sfqbdw}
	${shzt}
	${ssbzt}
	${xsbzt}
	)
	and xsbzt='已上报' and ssbzt='已上报' and shzt='未审核'

	
