mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj from GCGL_ABGC t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_ABGC 
where 1=1

	and jhid =#{jhid}
