mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj from GCGL_ABGC t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_ABGC 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj from GCGL_ABGC t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7 

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_ABGC 
where 1=1 and sfsj=7 

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_ABGC t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}

select t1.ID v_0,t3.gydw v_1,t3.xzqhmc v_2,t3.lxbm v_3,t3.lxmc v_4,t3.qdzh v_5,t3.zdzh v_6,0 v_7,0 v_8,'同意' v_9,#{tbyf} v_10,to_char(sysdate,'yyyy-mm-dd') v_11,#{tbr} v_12 from PLAN_ABGC t1 left join SCK_ABGC t2 on t1.SCKID=t2.SCKID left join XMK_ABGC t3 on t2.xmkid=t3.id left join (select * from gcgl_cgs where tbyf=#{tbyf}) c on t1.id=c.jhid where t1.spzt='1' and c.tbyf is null and t1.jhnf=#{jhnf}
${gydw}
and t1.kgzt like '%'||trim(#{kgzt})||'%'
and t1.jgzt like '%'||trim(#{jgzt})||'%'
and t3.lxmc like '%'||trim(#{lxmc})||'%'
order by t1.jhnf

select t2.id v_0, max(t2.sbnf) v_2,to_char(max(t2.jhkgsj),'yyyy-mm-dd') v_4,to_char(max(t2.jhwgsj),'yyyy-mm-dd') v_4,max(t2.xmmc) v_1,max(t2.pfztz) v_5,max(t2.jhsybzje) v_6,0 v_7,0 v_8,'同意' v_9,#{tbyf} v_10,to_char(sysdate,'yyyy-mm-dd') v_11,#{tbr} v_12 from PLAN_GCGJ t2 left join PLAN_LX_GCGJ t1 on t1.jhid=t2.id left join (select * from gcgl_cgs where tbyf=#{tbyf}) c on t2.id=c.jhid where 1=1 and spzt='1' and c.tbyf is null and t2.sbnf=#{jhnf}
${gydw}
and t2.kgzt like '%'||trim(#{kgzt})||'%'
and t2.jgzt like '%'||trim(#{jgzt})||'%'
and t1.lxmc like '%'||trim(#{lxmc})||'%'
group by t2.id

select t2.id v_0, max(t2.jhnf) v_2,to_char(max(t2.jhkgsj),'yyyy-mm-dd') v_3,to_char(max(t2.jhwgsj),'yyyy-mm-dd') v_4,max(t2.xmmc) v_1,max(t2.pftz) v_5,max(t2.jhsybbzje) v_6,0 v_7,0 v_8,'同意' v_9,#{tbyf} v_10,to_char(sysdate,'yyyy-mm-dd') v_11,#{tbr} v_12 from PLAN_GCSJ t2 left join PLAN_LX_GCSJ t1 on t1.jhid=t2.id left join (select * from gcgl_cgs where tbyf=#{tbyf}) c on t2.id=c.jhid where 1=1 and spzt='1' and c.tbyf is null and t2.jhnf=#{jhnf}
${gydw}
