select t2.id v_0, max(t2.sbnf) v_2,to_char(max(t2.jhkgsj),'yyyy-mm-dd') v_3,to_char(max(t2.jhwgsj),'yyyy-mm-dd') v_4,max(t2.xmmc) v_1,max(t2.totalsubsidyfund) v_5,max(t2.totalinvest) v_6,0 v_7,0 v_8,'同意' v_9,#{tbyf} v_10,to_char(sysdate,'yyyy-mm-dd') v_11,#{tbr} v_12 from PLAN_YHDZX t2 left join PLAN_LX_YHDZX t1 on t1.jhid=t2.id left join (select * from gcgl_cgs where tbyf=#{tbyf}) c on t2.id=c.jhid where 1=1 and spzt='1' and c.tbyf is null and t2.sbnf=#{jhnf}
