select * from ( select m.*,rownum rn from ( select t1.id v_0,t3.gydw v_1,t3.xzqhmc v_2,t3.lxbm v_3,t3.lxmc v_4,t3.qdzh v_5,t3.zdzh v_6,t3.qzlc v_7,t3.yhlc v_8,t3.gjxjnd v_9,t1.id v_10 from PLAN_ABGC t1 left join SCK_ABGC t2 on t1.SCKID=t2.SCKID left join XMK_ABGC t3 on t2.xmkid=t3.id where t1.spzt='1'
${gydw}
