mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj from GCGL_GCGZGJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZGJ 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj from GCGL_GCGZGJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7 

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZGJ 
where 1=1 and sfsj=7 

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_GCGZGJ t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}

select 
t1.lxmc,t1.lxbm,t1.xzqhdm,t1.xzqhmc,t1.jsdd,t1.gydw,t1.gydwbm,t1.bhnr,t1.qdzh,t1.zdzh,t1.qzlc,t1.yhlc,t1.ylmlx,t1.bz lxbz,t1.yjsdj

from PLAN_LX_GCGJ t1  where 1=1 and t1.jhid=#{jhid}

select * from ( select rownum as num,t.* from( select jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,jh.sfqxkg, l.gydw,l.xzqhmc,l.lxmc,l.lxbm,l.qdzh,l.zdzh,l.yhlc from PLAN_lx_GCGJ l right JOIN Plan_Gcgj jh on l.jhid=jh.id where 1=1 and spzt='1'
and jh.kgzt like '%'||trim(#{kgzt})||'%'
and jh.sbnf like '%'||trim(#{sbnf})||'%'
and jh.jgzt like '%'||trim(#{jgzt})||'%'
and ${tiaojian} like '%'||trim(#{shzt})||'%'
and jh.id in ( select l.jhid from PLAN_lx_GCGJ l where 1=1
${gydw}
and l.lxmc like '%'||trim(#{lxmc})||'%'
) ) t ) where num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

select * from (
		select rownum as num,t.* from(
		select jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,
		l.gydw,l.xzqhmc,l.lxmc,l.lxbm,l.qdzh,l.zdzh,l.yhlc 
		from PLAN_lx_GCGJ l right JOIN Plan_Gcgj jh on  l.jhid=jh.id left join (select * from gcgl_cgs where tbyf=#{tbyf}) c on jh.id=c.jhid
		where 1=1 and spzt='1' and jh.sbnf=#{sbnf}
		
				and jh.kgzt like '%'||trim(#{kgzt})||'%'
		
		
				and jh.jgzt like '%'||trim(#{jgzt})||'%'
		
		
			
				and c.tbyf is null
			
			
				and c.tbyf is not null
			
		and jh.id in (
				select l.jhid from PLAN_lx_GCGJ l
				where 1=1 
					
						${gydw}
					
					
							and l.lxmc like '%'||trim(#{lxmc})||'%'
					
					
				     )
				) t
		)
	where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

