mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.cscyj,t2.stz from GCGL_GCGZSJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZSJ 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.cscyj,t2.stz from GCGL_GCGZSJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZSJ 
where 1=1 and sfsj=7 

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_GCGZSJ t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}

select * from (
	select t.* from (
		select jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.jhnf,jh.xmmc,jh.yjwgsj,jh.xdsj,jh.sjkgsj,jh.sgdw,jh.jldw,jh.jsdw,jh.gsztz,jh.htje,
		jh.jhkgsj,jh.jhwgsj,jh.pftz,jh.jhsybbzje,jh.JHSYDFZCZJ,jh.sfqxkg,
		lx.gydw,lx.xzqhmc,lx.lxbm,lx.lxmc,lx.qdzh,lx.zdzh,lx.xmlc
	 	from (select * from (
		select p.*,rownum num from PLAN_GCSJ p where 1=1 
		and spzt='1'
		
				and P.kgzt like '%'||trim(#{kgzt})||'%'
		
		
				and P.jgzt like '%'||trim(#{jgzt})||'%'
		
		
				and P.jhnf like '%'||trim(#{xmnf})||'%'
		
		
				and ${tiaojian} like '%'||trim(#{shzt})||'%'
		
		and p.id in (
				select l.jhid from PLAN_lx_GCSJ l
				where 1=1 
					
						${gydw}
					
					
							and l.lxmc like '%'||trim(#{lxmc})||'%'
					
					
				     )
		)
		where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}
		) jh right join Plan_Lx_Gcsj lx on lx.jhid=jh.id
		where 1=1 
		
				) t
		)
