mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.stz,t2.cscyj from GCGL_SH t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_SH 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.stz,t2.cscyj from GCGL_SH t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7 

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_SH 
where 1=1 and sfsj=7

	and jhid =#{jhid}

mapper.update:
update GCGL_SH set wc_btz=#{wc_btz},wc_stz=#{wc_stz},wc_qttz=#{wc_qttz},zjdw_btz=#{zjdw_btz},zjdw_stz=#{zjdw_stz},zjdw_qttz=#{zjdw_qttz},bywcdc=#{bywcdc},bywcjc=#{bywcjc},bywcmc=#{bywcmc},wcqk=#{wcqk},kgdl=#{kgdl},qksm=#{qksm},sbyf=#{sbyf},ssdctc=#{ssdctc},bndsslc=#{bndsslc},wkglc=#{wkglc}
,dwyhdk=#{dwyhdk},dwsz=#{dwsz},dwgz=#{dwgz},yhdk=#{yhdk},gz=#{gz},sz=#{sz}
where id=#{id} and jhid=#{jhid}

mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_SH t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}
