mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.stz,t2.cscyj from GCGL_YHDZX t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_YHDZX 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.stz,t2.cscyj from GCGL_YHDZX t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7 

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_YHDZX 
where 1=1 and sfsj=7

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_YHDZX t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}

select * from (
	select rownum num ,t.*,s.sl sl  from JHSH_YHDZX t left join (select count(*) sl, xmid xmbm from lxsh_lx where jdbs='2' group by xmid) s on t.xmbm=s.xmbm where 1=1
	
			and t.kgzt like '%'||trim(#{kgzt})||'%'
	
	
			and t.jgzt like '%'||trim(#{jgzt})||'%'
	
	
			and t.xmbm like trim(#{xmnf})||'%'
	
	
			and ${tiaojian} like '%'||trim(#{shzt})||'%'
	
	and t.xmbm in (
			select xmid from lxsh_lx where 1=1 
			
			${gydw}
			
			
				and lxmc like '%'||trim(#{lxmc})||'%'
			
		)
	) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
	order by xmbm
