mapper.select:
		select * from 
			(
				select rownum as num,t.* from 
				(
					select  jh.id,jh.jhkgsj,jh.jhwgsj,jh.pfztz,jh.sbzt,jh.spzt,jh.jhnf,lx.gydw,lx.xzqhmc,lx.lxbm,lx.lxmc,lx.qdzh,lx.zdzh,lx.yhlc 
	    			from  Plan_Abgc jh left join Xmk_Abgc lx on jh.sckid=lx.id where 1=1 
	    			
	 					and jh.jhnf=#{jh.jhnf}
	 				
	 				
	 					and jh.sbzt=#{jh.sbzt}
	 				
	 				
	 					and jh.spzt=#{jh.spzt}
	 				
	 				
	 					and lx.gydw=#{lx.gydw}
	 				
	 				
	 					and lx.xzqhdm=#{lx.xzqhdm}
	 				
	 				
	 					and lx.lxmc like '%'||#{lx.lxmc}||'%'
	 				
	 				
	 					and lx.lxbm like #{lx.lxbm}||'%'
	 				
	 				
	 					and lx.lxjsdj = #{lx.lxjsjd}
	 				
	    			order by jh.jhnf desc
	    		) t
    		)
    	where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

	select count(*) from PLAN_abgc jh where 1=1
	
 		and jh.jhnf=#{jh.jhnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
 					and lx.gydw=#{lx.gydw}
 				
 				
 					and lx.xzqhdm=#{lx.xzqhdm}
 				
 				
 					and lx.lxmc like '%'||#{lx.lxmc}||'%'
 				
 				
 					and lx.lxbm like #{lx.lxbm}||'%'
 				
 				
 					and lx.lxjsdj = #{lx.lxjsjd}
