mapper.select:
	select * from (
		select rownum as num,t.* from(
		select jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,
		l.gydw,l.xzqhmc,l.lxmc,l.lxbm,l.qdzh,l.zdzh,l.yhlc 
		from PLAN_lx_GCGJ l right JOIN Plan_Gcgj jh on  l.jhid=jh.id
		where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 					and 2=2
 				
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
			and jh.id in (
				select l.jhid from PLAN_lx_GCGJ l 
				where 1=1 
					
						and l.gydwdm like #{l.gydwdm}
					
					
						and l.xzqhdm like #{l.xzqhdm}
					
					
						and l.lxmc like '%'||#{l.lxmc}||'%'
					
					
						and l.yjsdj=#{l.yjsdj}
					
					
						and l.lxbm like #{l.lxbm}
					
					
				     and l.xzqhdm in (
				     	select B.XZQHDM from BT_XZQHTSDQ b 
				     	WHERE and B.AREACODE=#{l.tsdqbm}
				     	 )
				     
				     ) order by jh.jh_sbthcd,jh.sbnf
				) t
		)
	where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

	select * from (
		select rownum as num,t.* from(
		select jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,
		l.gydw,l.xzqhmc,l.lxmc,l.lxbm,l.qdzh,l.zdzh,l.yhlc 
		from PLAN_lx_GCGJ l right JOIN Plan_Gcgj jh on  l.jhid=jh.id
		where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 					and 2=2
 				
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
			and jh.id in (
				select l.jhid from PLAN_lx_GCGJ l 
				where 1=1 
					
						and l.gydwdm like #{l.gydwdm}
					
					
						and l.xzqhdm like #{l.xzqhdm}
					
					
						and l.lxmc like '%'||#{l.lxmc}||'%'
					
					
						and l.yjsdj=#{l.yjsdj}
					
					
						and l.lxbm like #{l.lxbm}
					
					
				     and l.xzqhdm in (
				     	select B.XZQHDM from BT_XZQHTSDQ b 
				     	WHERE and B.AREACODE=#{l.tsdqbm}
				     	 )
				     
				     ) order by jh.jh_sbthcd,jh.sbnf
				) t
		)

	select count(*) from PLAN_GCGJ jh where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
			and jh.id in (
				select l.jhid from PLAN_lx_GCGJ l
				where 1=1 
					
						and l.gydwdm like '%'||#{l.gydwdm}||'%'
					
					
						and l.xzqhdm like #{l.xzqhdm}
					
					
						and l.lxmc like '%'||#{l.lxmc}||'%'
					
					
						and l.yjsdj=#{l.yjsdj}
					
					
						and l.lxbm like #{l.lxbm}
					
					
				     and l.xzqhdm in (
				     	select B.XZQHDM from BT_XZQHTSDQ b 
				     	WHERE 1=1 
							and B.AREACODE=#{l.tsdqbm}
				     	 )
				    
				     )

	select count(id) as id,sum(pfztz) as pfztz,sum(jhsybzje) as jhsybzje,sum(jhsydfzcje) as jhsydfzcje
		,sum(qzlc) as qzlc,sum(yhlc) as yhlc from(
		select jh.id as id,jh.pfztz as pfztz,jh.jhsybzje as jhsybzje,jh.jhsydfzcje as jhsydfzcje,
		sum(lx.qzlc) as qzlc,sum(lx.yhlc) as yhlc
		from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id)
		where 1=1
		
			and lx.gydwdm like #{lx.gydwdm}
		
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
		group by jh.id,jh.pfztz,jh.jhsybzje,jh.jhsydfzcje)

mapper.update:
update plan_gcgj jh set 

	jh.GKBGMC=#{gkbgmc},


	jh.gkbgwj=#{gkbgwj}


	jh.SJSGTMC=#{sjsgtmc},


	jh.sjsgtwj=#{sjsgtwj}

where jh.id=#{id}

mapper.select:
select count(*) from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) 
where lx.lxbm = #{lxbm} and lx.qdzh<#{zdzh} and lx.zdzh>#{qdzh}
 and jh.sbnf=#{jhid}

select * from plan_flwbzbz f where 

	f.xmlx is null


	f.xmlx=#{xmlx}


	and f.gldj is null


	and f.gldj=#{gldj}


	and f.jsdj is null


	and f.jsdj=#{jsdj}


	and f.tsdq is null


	and f.tsdq =#{tsdq}

		select (r.kgzt+r.jgzt) as id,count(r.kgzt+r.jgzt) as name,sum(r.jhsybzje) as text,sum(btz+qttz) as parent from (
       		select jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje,sum(nvl(gcgl.wc_btz,0)) as btz,sum(nvl(gcgl.wc_qttz,0)) as qttz
       			from gcgl_gcgzgj gcgl right join (
            		select distinct jh.id,jh.kgzt,jh.jgzt,jh.jhsybzje from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) where lx.xzqhdm like #{xzqhdm} and jh.sbnf=#{nf} and jh.spzt='1' and jh.jh_sbthcd='6')
       		jh_lx on (gcgl.jhid=jh_lx.id) group by jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje
		) r group by r.kgzt,r.jgzt order by r.kgzt,r.jgzt

		select nvl(sum(jhsybzje),0) as text,nvl(sum(btz+qttz),0) as parent from (
       		select jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje,sum(nvl(gcgl.wc_btz,0)) as btz,sum(nvl(gcgl.wc_qttz,0)) as qttz
       			from gcgl_gcgzgj gcgl right join (
            		select distinct jh.id,jh.kgzt,jh.jgzt,jh.jhsybzje 
            		from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) 
            		where jh.spzt='1' and jh.jh_sbthcd='6'
            		
            			and lx.xzqhdm like #{xzqhdm}
            		)
       		jh_lx on (gcgl.jhid=jh_lx.id) group by jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje
		) r
