mapper.select:
	select t2.*,t1.gydw,t1.xzqhmc,t1.lxmc,t1.lxbm,t1.qdzh,t1.zdzh,t1.yhlc from PLAN_lx_GCGJ t1 right join (
		select rownum as num,t.* from (
		select distinct jh.tbbm,jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz
		
		from PLAN_lx_GCGJ lx right JOIN Plan_Gcgj jh on  lx.jhid=jh.id
		where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
 		
 			and jh.sfylsjl=#{jh.sfylsjl}
	 	
 		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.lxbm like #{lx.lxbm}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
		
		 order by jh.sbnf desc,jh.jh_sbthcd
		 ) t order by t.sbnf desc,t.jh_sbthcd
		) t2 on t1.jhid=t2.id where  t2.num <= (#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}

	select * from (
		select rownum as num,t.* from(
		select jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,
		lx.gydw,lx.xzqhmc,lx.lxmc,lx.lxbm,lx.qdzh,lx.zdzh,lx.yhlc 
		from PLAN_lx_GCGJ lx right JOIN Plan_Gcgj jh on  lx.jhid=jh.id
		where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
 			and jh.kgzt=#{jh.kgzt}
 		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
 		
 			and jh.sfylsjl=#{jh.sfylsjl}
	 	
 		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.lxbm like #{lx.lxbm}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
		
		order by jh.jh_sbthcd,jh.sbnf) t
		)

		select count(*) from PLAN_lx_GCGJ lx right JOIN Plan_Gcgj jh on lx.jhid=jh.id where 1=1
			
			and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
	 		and jh.kgzt=#{jh.kgzt}
	 	
		
	 		and jh.jgzt=#{jh.jgzt}
	 	
	 	
	 		and jh.sfylsjl=#{jh.sfylsjl}
		
 		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.lxbm like #{lx.lxbm}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})

		select count(id) as id,sum(pfztz) as pfztz,sum(jhsybzje) as jhsybzje,sum(jhsydfzcje) as jhsydfzcje
			,sum(qzlc) as qzlc,sum(yhlc) as yhlc from(
			select jh.id as id,jh.pfztz as pfztz,jh.jhsybzje as jhsybzje,jh.jhsydfzcje as jhsydfzcje,
			sum(lx.qzlc) as qzlc,sum(lx.yhlc) as yhlc
			from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id)
			where 1=1
			
			and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
	 		and jh.kgzt=#{jh.kgzt}
	 	
		
	 		and jh.jgzt=#{jh.jgzt}
	 	
	 	
	 		and jh.sfylsjl=#{jh.sfylsjl}
		
 		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.lxbm like #{lx.lxbm}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
		
			group by jh.id,jh.pfztz,jh.jhsybzje,jh.jhsydfzcje)

