mapper.select:
select * from ( select g.roadname as lxmc,g.roadcode as lxbm,min(g.roadstart) as qdzh,max(g.roadends) as zdzh, (max(g.roadends)-min(g.roadstart)) as qzlc,g.f114 as yjsdj,g.f116 as ylmlx from JXJCK_2013.GPSROAD g where substr(roadcode,1,1) in('G','S','X') and roadcode like #{lxbm}||'%' and roadcode like '%'||rtrim(#{xzqhdm},0)||'%'
and g.f116=#{ylmlx}
group by roadname,roadcode,g.f114,g.f116) g1 where g1.qdzh<=#{qdzh} and g1.zdzh>=#{zdzh}

		select gj.gjCon+shuih.shCon+sj.sjCon+yh.yhCon from 
		(select count(*) gjCon from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) 
		where lx.lxbm = #{lxbm} and lx.qdzh<#{zdzh} and lx.zdzh>#{qdzh}
		and jh.sbnf=#{jhid}) gj,
		(select count(*) shCon from plan_lx_shuih lx right join plan_shuih jh on (lx.jhid=jh.id) 
		where lx.lxbm=#{lxbm} and lx.qdzh<=#{zdzh} and lx.zdzh>=#{qdzh} 
		and jh.sbnf=#{jhid}) shuih,
		(select count(jh.id) sjCon from plan_lx_gcsj lx right join plan_gcsj jh on (lx.jhid=jh.id) 
		where lx.lxbm=#{lxbm} and lx.qdzh<#{zdzh} and lx.zdzh>#{qdzh}
		and jh.jhnf=#{jhid}) sj,
		(select count(jh.id) yhCon from plan_lx_yhdzx lx right join plan_yhdzx jh on (lx.jhid=jh.id) 
    	where lx.lxbm=#{lxbm} and lx.qdzh<#{zdzh} and lx.zdzh>#{qdzh}
    	and jh.sbnf=#{jhid}) yh

select * from plan_flwbzbz f where 

	f.xmlx is null


	f.xmlx=#{xmlx}


	and f.gldj is null


	and f.gldj=#{gldj}


	and f.jsdj is null


	and f.jsdj=#{jsdj}


	and f.tsdq is null


	and f.tsdq =#{tsdq}

select id,sum(text) text,sum(name) name,sum(stz) parent,count(id) bmid from ( select jh.sbnf as id,lx.xzqhdm,jh.pfztz as text,jh.jhsybzje as name,jh.jhsydfzcje stz,jh.id as jhid,'路面改建' as xmlx from plan_gcgj jh,( select l.jhid,replace(WMSYS.WM_CONCAT(to_char(l.xzqhdm)),',','、') as xzqhdm from plan_lx_gcgj l group by l.jhid ) lx where jh.id=lx.jhid union all select sj.jhnf,sjlx.xzqhdm,sj.pftz,sj.jhsybbzje,sj.jhsydfzczj,sj.id,'路面升级' from plan_gcsj sj,( select sjl.jhid,replace(WMSYS.WM_CONCAT(to_char(sjl.xzqhdm)),',','、') as xzqhdm from plan_lx_gcsj sjl group by sjl.jhid) sjlx where sj.id=sjlx.jhid union all select jh.sbnf,lx.xzqhdm,jh.pfztz,jh.jhsybzje,jh.jhsydfzcje,jh.id,'水毁项目' from plan_shuih jh,( select l.jhid,replace(WMSYS.WM_CONCAT(to_char(l.xzqhdm)),',','、') as xzqhdm from plan_lx_shuih l group by l.jhid ) lx where jh.id=lx.jhid union all select yh.sbnf,lx.xzqhdm,yh.totalinvest,yh.totalsubsidyfund,yh.totalplacefund,yh.id,'养护大中修' from plan_yhdzx yh,( select yhl.jhid,replace(WMSYS.WM_CONCAT(to_char(yhl.xzqhdm)),',','、') as xzqhdm from plan_lx_yhdzx yhl group by yhl.jhid) lx where yh.id=lx.jhid union all select wq.sbnf,wqlx.xzqhdm,wq.pfztz,wq.jhsybzje,wq.jhsydfzcje,wq.id,'危桥改造' from plan_wqgz wq left join sck_wqgz wqsc on (wq.sckid=wqsc.sckid) left join xmk_wqgz wqlx on (wqsc.xmkid=wqlx.id) union all select abjh.jhnf,ablx.xzqhdm,abjh.pfztz,abjh.jhsybbzje,abjh.jhsydfzczj,abjh.id,'安保工程' from plan_abgc abjh left join sck_abgc absc on (abjh.sckid=absc.sckid) left join xmk_abgc ablx on (absc.xmkid=ablx.id) union all select zhjh.sbnf,zhlx.xzqhdm,zhjh.pfztz,zhjh.jhsybzje,zhjh.jhsydfzcje,zhjh.id,'灾害防治' from plan_zhfz zhjh left join sck_zhfz zhsc on (zhjh.sckid=zhsc.sckid) left join xmk_zhfz zhlx on (zhsc.xmkid=zhlx.id)) where 1=1 and xzqhdm like substr(#{xzqhdm},0,4)||'%'
and id>=#{start} and id<=#{end}
group by id order by id

select id,sum(text) text,sum(name) name,sum(stz) parent,count(id) bmid from ( select jh.sbnf as id,lx.xzqhdm,jh.pfztz as text,jh.jhsybzje as name,jh.jhsydfzcje stz,jh.id as jhid,'路面改建' as xmlx from plan_gcgj jh,( select l.jhid,replace(WMSYS.WM_CONCAT(to_char(l.xzqhdm)),',','、') as xzqhdm from plan_lx_gcgj l group by l.jhid ) lx where jh.id=lx.jhid union all select sj.jhnf,sjlx.xzqhdm,sj.pftz,sj.jhsybbzje,sj.jhsydfzczj,sj.id,'路面升级' from plan_gcsj sj,( select sjl.jhid,replace(WMSYS.WM_CONCAT(to_char(sjl.xzqhdm)),',','、') as xzqhdm from plan_lx_gcsj sjl group by sjl.jhid) sjlx where sj.id=sjlx.jhid union all select jh.sbnf,lx.xzqhdm,jh.pfztz,jh.jhsybzje,jh.jhsydfzcje,jh.id,'水毁项目' from plan_shuih jh,( select l.jhid,replace(WMSYS.WM_CONCAT(to_char(l.xzqhdm)),',','、') as xzqhdm from plan_lx_shuih l group by l.jhid ) lx where jh.id=lx.jhid union all select yh.sbnf,lx.xzqhdm,yh.totalinvest,yh.totalsubsidyfund,yh.totalplacefund,yh.id,'养护大中修' from plan_yhdzx yh,( select yhl.jhid,replace(WMSYS.WM_CONCAT(to_char(yhl.xzqhdm)),',','、') as xzqhdm from plan_lx_yhdzx yhl group by yhl.jhid) lx where yh.id=lx.jhid union all select wq.sbnf,wqlx.xzqhdm,wq.pfztz,wq.jhsybzje,wq.jhsydfzcje,wq.id,'危桥改造' from plan_wqgz wq left join sck_wqgz wqsc on (wq.sckid=wqsc.sckid) left join xmk_wqgz wqlx on (wqsc.xmkid=wqlx.id) union all select abjh.jhnf,ablx.xzqhdm,abjh.pfztz,abjh.jhsybbzje,abjh.jhsydfzczj,abjh.id,'安保工程' from plan_abgc abjh left join sck_abgc absc on (abjh.sckid=absc.sckid) left join xmk_abgc ablx on (absc.xmkid=ablx.id) union all select zhjh.sbnf,zhlx.xzqhdm,zhjh.pfztz,zhjh.jhsybzje,zhjh.jhsydfzcje,zhjh.id,'灾害防治' from plan_zhfz zhjh left join sck_zhfz zhsc on (zhjh.sckid=zhsc.sckid) left join xmk_zhfz zhlx on (zhsc.xmkid=zhlx.id)) where 1=1 and xmlx = #{xmlx}
and id>=#{start} and id<=#{end}
and xzqhdm like #{xzqhdm}
group by id order by id

		select (r.kgzt+r.jgzt) as id,count(r.kgzt+r.jgzt) as name,sum(r.jhsybzje) as text,sum(btz+qttz) as parent from (
       		select jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje,sum(nvl(gcgl.wc_btz,0)) as btz,sum(nvl(gcgl.wc_qttz,0)) as qttz
       			from gcgl_gcgzgj gcgl right join (
            		select distinct jh.id,jh.kgzt,jh.jgzt,jh.jhsybzje from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) where lx.xzqhdm like #{xzqhdm} and jh.sbnf=#{nf} and jh.spzt='1' and jh.jh_sbthcd='6')
       		jh_lx on (gcgl.jhid=jh_lx.id) group by jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje
		) r group by r.kgzt,r.jgzt order by r.kgzt,r.jgzt

		select nvl(sum(jhsybzje),0) as text,nvl(sum(btz+qttz),0) as parent from (
       		select jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje,sum(nvl(gcgl.wc_btz,0)) as btz,sum(nvl(gcgl.wc_qttz,0)) as qttz
       			from gcgl_gcgzgj gcgl right join (
            		select distinct jh.id,jh.kgzt,jh.jgzt,jh.jhsybzje 
            		from plan_lx_gcgj lx right join plan_gcgj jh on (lx.jhid=jh.id) 
            		where jh.spzt='1' and jh.jh_sbthcd='6'
            		
            			and lx.xzqhdm like #{xzqhdm}
            		)
       		jh_lx on (gcgl.jhid=jh_lx.id) group by jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybzje
		) r
