mapper.select:
select * from (
	select rownum as num,t.* from(
	select jh.sfylsjl,jh.jgzt,jh.kgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,
	l.gydw,l.xzqhmc,l.lxmc,l.lxbm,l.qdzh,l.zdzh,l.yhlc 
	from PLAN_lx_GCGJ l right JOIN Plan_Gcgj jh on  l.jhid=jh.id
	where 1=1
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
		and jh.sbnf =#{jh.sbnf}
	
	
		and jh.sbzt=#{jh.sbzt}
	
	
		and jh.spzt=#{jh.spzt}
	
		and jh.id in (
			select l.jhid from PLAN_lx_GCGJ l 
			where 1=1 
				
					and l.gydwdm like #{l.gydwdm}
				
				
					and l.xzqhdm like #{l.xzqhdm}
				
				
					and l.lxmc like '%'||#{l.lxmc}||'%'
				
				
					and l.yjsdj=#{l.yjsdj}
				
				
					and l.lxbm like #{l.lxbm}
				
			     and l.xzqhdm in (
			     	select B.XZQHDM from BT_XZQHTSDQ b 
			     	WHERE 1=1 
			     	
						B.AREACODE=#{l.tsdqbm}
					
			     	 )
			     ) order by jh.sbzt,jh.spzt,jh.sbnf desc
			) t
	)
where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

select count(*) from PLAN_GCGJ jh where 1=1
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
		and jh.sbnf =#{jh.sbnf}
	
	
		and jh.sbzt=#{jh.sbzt}
	
	
		and jh.spzt=#{jh.spzt}
	
		and jh.id in (
			select l.jhid from PLAN_lx_GCGJ l
			where 1=1 
				
					and l.gydwdm like '%'||#{l.gydwdm}||'%'
				
				
					and l.xzqhdm like #{l.xzqhdm}
				
				
					and l.lxmc like '%'||#{l.lxmc}||'%'
				
				
					and l.yjsdj=#{l.yjsdj}
				
				
					and l.lxbm like #{l.lxbm}
				
			     and l.xzqhdm in (
			     	select B.XZQHDM from BT_XZQHTSDQ b 
			     	WHERE 1=1 
			     	
						B.AREACODE=#{l.tsdqbm}
					
			     	 )
			     )
