mapper.select:
	select * from (
		select rownum as num,t.* from (	
			select jh.tbbm,jh.xmmc,jh.jhsydfzcje,jh.JHSYBZJE,jh.sfylsjl,jh.kgzt,jh.jgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,jh.xdsj,
      		lx.gydw,lx.xzqhmc,lx.lxmc,lx.lxbm,lx.qdzh,lx.zdzh,lx.yhlc,lx.ylmlx 
      		from PLAN_lx_shuih lx right JOIN plan_shuih jh on  lx.jhid=jh.id 
			where 1=1
				
		 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
			
			
				and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
			
			
				and jh.sbnf =#{jh.sbnf}
			
			
				and jh.sbzt=#{jh.sbzt}
			
			
				and jh.spzt=#{jh.spzt}
			
			
				and jh.kgzt=#{jh.kgzt}
 			
 			
 				and jh.jgzt=#{jh.jgzt}
 			
 			
		 		and jh.sfylsjl=#{jh.sfylsjl}
			
			
				and ${lx.gydwdm}
			
			
				and ${lx.xzqhdm}
			
			
				and lx.lxmc like '%'||#{lx.lxmc}||'%'
			
			
				and lx.yjsdj=#{lx.yjsdj}
			
			
				and lx.lxbm like #{lx.lxbm}
			
			
				and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
			
			order by jh.jh_sbthcd,jh.sbnf desc) t order by t.jh_sbthcd,t.sbnf desc
		)
		where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

	select * from (
		select rownum as num,t.* from (	
			select jh.xmmc,jh.sfylsjl,jh.kgzt,jh.jgzt,jh.jh_sbthcd,jh.id,jh.sbzt,jh.spzt,jh.sbnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,jh.xdsj,
      		lx.gydw,lx.xzqhmc,lx.lxmc,lx.lxbm,lx.qdzh,lx.zdzh,lx.yhlc,lx.qzlc,lx.ylmlx
      		from PLAN_lx_shuih lx right JOIN plan_shuih jh on  lx.jhid=jh.id 
			where 1=1
				
		 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
			
			
				and jh.sbnf =#{jh.sbnf}
			
			
				and jh.kgzt=#{jh.kgzt}
 			
 			
 				and jh.jgzt=#{jh.jgzt}
 			
 			
		 		and jh.sfylsjl=#{jh.sfylsjl}
			
			
				and ${lx.gydwdm}
			
			
				and ${lx.xzqhdm}
			
			
				and lx.lxmc like '%'||#{lx.lxmc}||'%'
			
			
				and lx.yjsdj=#{lx.yjsdj}
			
			
				and lx.lxbm like #{lx.lxbm}
			
			
				and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
			
			order by jh.jh_sbthcd,jh.sbnf desc
			) t)

	select count(*) from PLAN_lx_shuih lx right JOIN plan_shuih jh on  lx.jhid=jh.id  where 1=1
		
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
		and jh.sbnf =#{jh.sbnf}
	
	
		and jh.sbzt=#{jh.sbzt}
	
	
		and jh.spzt=#{jh.spzt}
	
	
		and jh.kgzt=#{jh.kgzt}
 	
 	
 		and jh.jgzt=#{jh.jgzt}
 	
 	
		and jh.sfylsjl=#{jh.sfylsjl}
	
	
		and ${lx.gydwdm}
	
	
		and ${lx.xzqhdm}
	
	
		and lx.lxmc like '%'||#{lx.lxmc}||'%'
	
	
		and lx.yjsdj=#{lx.yjsdj}
	
	
		and lx.lxbm like #{lx.lxbm}
	
	
		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})

select count(id) as id,TO_CHAR(sum(qzlc),'FM9999999990.099') as qzlc,TO_CHAR(sum(yhlc),'FM9999999990.099') as yhlc,sum(pfztz) as pfztz,
sum(jhsybzje) as jhsybzje,sum(jhsydfzcje) as jhsydfzcje from 
	(select count(jh.id) as id,sum(lx.qzlc) as qzlc,sum(lx.yhlc) as yhlc,sum(jh.pfztz) as pfztz,
		sum(jh.jhsybzje) as jhsybzje,sum(jh.jhsydfzcje) as jhsydfzcje
		from plan_lx_shuih lx right join plan_shuih jh on (lx.jhid=jh.id)
		where 1=1
			
			and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.kgzt=#{jh.kgzt}
	 	
	 	
	 		and jh.jgzt=#{jh.jgzt}
	 	
	 	
			and jh.sfylsjl=#{jh.sfylsjl}
		
		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.lxbm like #{lx.lxbm}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdqbm})
		
		group by jh.id)

