	 					and lx.qlmc like '%'||#{lx.qlmc}||'%'
	 				
	 				
	 					and lx.akjfl like #{lx.akjfl}||'%'
	 				
	
	
		select distinct t.sbnf as text from PLAN_wqgz t order by t.sbnf
	
	
