mapper.select:
	select * from (
		select rownum as num,t.* from (
    		select * from plan_yhdzx jh left join plan_lx_yhdzx lx on (jh.id=lx.jhid) 
       		where 1=1 
       			
		 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
			
			
				and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
			
			
				and jh.sbnf =#{jh.sbnf}
			
			
				and jh.sbzt=#{jh.sbzt}
			
			
				and jh.spzt=#{jh.spzt}
			
			
				and jh.kgzt=#{jh.kgzt}
 			
 			
 				and jh.jgzt=#{jh.jgzt}
 			
			
				and ${lx.gydwdm}
			
			
				and ${lx.xzqhdm}
			
			
				and lx.lxmc like '%'||#{lx.lxmc}||'%'
			
			
				and lx.lxbm like '%'||#{lx.lxbm}||'%'
			
			
				and lx.yjsdj=#{lx.yjsdj}
			
			
				and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
			
       		order by jh.sbnf desc,jh.jh_sbthcd) t order by t.sbnf desc,t.jh_sbthcd)
	where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

	select * from (
		select rownum as num,t.* from (
    		select * from plan_yhdzx jh left join plan_lx_yhdzx lx on (jh.id=lx.jhid) 
       		where 1=1 
       			
		 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
			
			
				and jh.sbnf =#{jh.sbnf}
			
			
				and jh.kgzt=#{jh.kgzt}
 			
 			
 				and jh.jgzt=#{jh.jgzt}
 			
			
				and ${lx.gydwdm}
			
			
				and ${lx.xzqhdm}
			
			
				and lx.lxmc like '%'||#{lx.lxmc}||'%'
			
			
				and lx.lxbm like '%'||#{lx.lxbm}||'%'
			
			
				and lx.yjsdj=#{lx.yjsdj}
			
			
				and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
			
       		order by jh.sbnf desc,jh.jh_sbthcd) t order by t.sbnf desc,t.jh_sbthcd)

	select count(*) from plan_yhdzx jh left join plan_lx_yhdzx lx 
	on (jh.id=lx.jhid) where 1=1
			
	 		and jh.jh_sbthcd=#{jh.jh_sbthcd}
		
		
			and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
		
		
			and jh.sbnf =#{jh.sbnf}
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and jh.kgzt=#{jh.kgzt}
		
 		
 			and jh.jgzt=#{jh.jgzt}
 		
		
			and ${lx.gydwdm}
		
		
			and ${lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.lxbm like '%'||#{lx.lxbm}||'%'
		
		
			and lx.yjsdj=#{lx.yjsdj}
		
		
			and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})

mapper.update:
update plan_lx_yhdzx lx set lx.lmjg=#{lx.lmjg},lx.aym=#{lx.aym},
lx.asl=#{lx.asl},lx.glf=#{lx.glf}
where lx.lxid=#{lx.lxid}

update plan_yhdzx jh set jh.sbnf=#{jh.sbnf}
,jh.jhkgsj=#{jh.jhkgsj}
,jh.jhwgsj=#{jh.jhwgsj}
,jh.xdsj=#{jh.xdsj}
,jh.xmmc=#{jh.xmmc}
,jh.classify=#{jh.classify}
,jh.aadt=#{jh.aadt}
,jh.pqi=#{jh.pqi}
,jh.dianceng=#{jh.dianceng}
,jh.jiceng=#{jh.jiceng}
,jh.surface=#{jh.surface}
,jh.reportingfee=#{jh.reportingfee}
,jh.mark=#{jh.mark}
