mapper.select:
		select * from 
			(
				select rownum as num,t.* from (
					select * from plan_zhfz jh left join sck_zhfz sc on (jh.sckid=sc.sckid)
					left join xmk_zhfz lx on (sc.xmkid=lx.id) where 1=1
				 	
						and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
					
				 	
	 					and jh.sbnf=#{jh.sbnf}
	 				
	 				
	 					and jh.sbzt=#{jh.sbzt}
	 				
	 				
	 					and jh.spzt=#{jh.spzt}
	 				
	 				
	 					and lx.gydwdm like #{lx.gydwdm}
	 				
	 				
	 					and lx.xzqhdm like #{lx.xzqhdm}
	 				
	 				
	 					and lx.lxmc like '%'||#{lx.lxmc}||'%'
	 				
	 				
	 					and lx.lxbm like #{lx.lxbm}||'%'
	 				
	 				
	 					and lx.lxjsdj = #{lx.lxjsjd}
	 				
				 	order by jh.sbzt,jh.spzt,jh.sbnf desc
			 	) t
			)
 			where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

	select count(*) from plan_zhfz jh left join sck_zhfz sc on (jh.sckid=sc.sckid) 
	left join xmk_zhfz lx on (sc.xmkid=lx.id) where 1=1
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
 		and jh.sbnf=#{jh.sbnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
 		and lx.gydwdm like #{lx.gydwdm}
 	
 	
 		and lx.xzqhdm like #{lx.xzqhdm}
 	
 	
 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
 	
 	
 		and lx.lxbm like #{lx.lxbm}||'%'
 	
 	
 		and lx.lxjsdj = #{lx.lxjsjd}

	select decode(jh.sbzt,0,'未上报',decode(jh.spzt,0,'上报待审批','已审批')) v_0,jh.sbnf v_1,to_char(jh.jhkgsj,'yyyy-mm-dd') v_2,to_char(jh.jhwgsj,'yyyy-mm-dd') v_3,lx.gydw v_4,lx.xzqhmc v_5,lx.lxbm v_6,lx.lxmc v_7,lx.qdzh v_8,lx.zdzh v_9,'' v_10,jh.pfztz v_11 
	from plan_zhfz jh
	left join sck_zhfz sc on (jh.sckid = sc.sckid)
	left join xmk_zhfz lx on (sc.xmkid = lx.id)
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
 	
			and jh.sbnf=#{jh.sbnf}
		
		
			and jh.sbzt=#{jh.sbzt}
		
		
			and jh.spzt=#{jh.spzt}
		
		
			and lx.gydwdm like #{lx.gydwdm}
		
		
			and lx.xzqhdm like #{lx.xzqhdm}
		
		
			and lx.lxmc like '%'||#{lx.lxmc}||'%'
		
		
			and lx.lxbm like #{lx.lxbm}||'%'
		
		
			and lx.lxjsdj = #{lx.lxjsjd}
		
	order by jh.sbnf desc

select count(p.id) as id,sum(x.qzlc) as qzlc,sum(x.yhlc) as yhlc,
sum(p.pfztz) as pfztz,sum(p.jhsybzje) as jhsybzje,sum(p.jhsydfzcje) as jhsydfzcje
from xmk_zhfz x right join sck_zhfz s on (x.id=s.xmkid) right join plan_zhfz p on (s.sckid=p.sckid)
