mapper.select:
			select * from (
		select rownum num ,t.*,decode(JH_SBTHCD,#{sbthcd},'未上报',decode(SBZT,0,'未上报',1,'已上报')) sbzt2 from
		 (select t.*,xm.xzqhdm,xm.gydwbm,xm.lxmc,xm.qlmc,xm.jsdj,xm.akjfl,xm.lxbm, 
		 xm.xzqhmc,xm.gydw,xm.qlbh,xm.tsdq,sc.sck_qlbh,sc.sck_qlzxzh
		 from plan_wqgz t join sck_wqgz sc on t.sckid=sc.sckid join xmk_wqgz xm on xmkid=xm.id where 1=1 and t.jh_sbthcd>=#{sbthcd}
		 		
 					and t.sfylrbwqk like '%'||(#{sfylrbwqk})||'%'
				
 				
 					and xm.tsdq like '%'||#{tsdq}||'%'
 				) t where 1=1 and gydwbm like '1%'
		
			${gydw}
		
		
			${xzqhdm}
		
		
			and lxmc like '%'||(#{lxmc})||'%'
		
		
			and qlmc like '%'||(#{qlmc})||'%'
		
		
			and jsdj like '%'||(#{jsdj})||'%'
		
		
			and akjfl like #{akjfl}||'%'
		
		
 			and sfylsjl like #{sfylsjl}||'%'
 		
 		
			and lxbm like (#{lxbm})||'%'
		
		
			and sbnf like '%'||(#{sbnf})||'%'
		
		
			and decode(JH_SBTHCD,#{sbthcd},'未上报',decode(SBZT,0,'未上报',1,'已上报'))=#{sbzt}
		
		
			and decode(SPZT,0,'未审核',1,'已审核')=#{shzt}
		
		) t1 where t1.num <=(#{page} * #{rows}) and
		t1.num>(#{page}-1)*#{rows}
		order by decode(JH_SBTHCD,#{sbthcd},'未上报',decode(SBZT,0,'未上报',1,'已上报')),spzt desc

		select count(*) from 
		(select t.*,xm.xzqhdm,xm.gydwbm,xm.lxmc,xm.qlmc,xm.jsdj,xm.akjfl,xm.lxbm from plan_wqgz t join sck_wqgz sc on t.sckid=sc.sckid join xmk_wqgz xm on xmkid=xm.id where 1=1 and t.jh_sbthcd>=#{sbthcd} 
		 		
 					and t.sfylrbwqk like '%'||(#{sfylrbwqk})||'%'
				
 				
 					and xm.tsdq like '%'||#{tsdq}||'%'
 				) t where 1=1 and gydwbm like '1%'
		
			${gydw}
		
		
			${xzqhdm}
		
		
			and lxmc like '%'||(#{lxmc})||'%'
		
		
			and qlmc like '%'||(#{qlmc})||'%'
		
		
			and jsdj like '%'||(#{jsdj})||'%'
		
		
			and akjfl like #{akjfl}||'%'
		
		
 			and sfylsjl like #{sfylsjl}||'%'
 		
 		
			and lxbm like (#{lxbm})||'%'
		
		
			and sbnf like '%'||(#{sbnf})||'%'
		
		
			and decode(JH_SBTHCD,#{sbthcd},'未上报',decode(SBZT,0,'未上报',1,'已上报'))=#{sbzt}
		
		
			and decode(SPZT,0,'未审核',1,'已审核')=#{shzt}

		select count(*) sl,sum(pfztz) pfztz,decode(sfylrbwqk,'是',decode(sum(jhsybzje),null,0,sum(jhsybzje))) jhsybzje,sum(jhsydfzcje) jhsydfzcje,decode(sfylrbwqk,'否',decode(sum(shengbz),null,0,sum(shengbz))) shengbz,decode(sum(xdzj),null,0,sum(xdzj)) xdzj from 
		(select t.*,xm.xzqhdm,xm.gydwbm,xm.lxmc,xm.qlmc,xm.jsdj,xm.akjfl,xm.lxbm,zj.xdzj from plan_wqgz t join sck_wqgz sc on t.sckid=sc.sckid join xmk_wqgz xm on xmkid=xm.id left join plan_zjxd zj on t.id=zj.xmid where 1=1  and t.jh_sbthcd>=#{sbthcd} 
		 		
 					and t.sfylrbwqk like '%'||(#{sfylrbwqk})||'%'
				
 				
 					and xm.tsdq like '%'||#{tsdq}||'%'
 				) t where 1=1 and gydwbm like '1%'
		
			${gydw}
		
		
			${xzqhdm}
		
		
			and lxmc like '%'||(#{lxmc})||'%'
		
		
			and qlmc like '%'||(#{qlmc})||'%'
		
		
			and jsdj like '%'||(#{jsdj})||'%'
		
		
			and akjfl like #{akjfl}||'%'
		
		
 			and sfylsjl like #{sfylsjl}||'%'
 		
 		
			and lxbm like (#{lxbm})||'%'
		
		
			and sbnf like '%'||(#{sbnf})||'%'
		
		
			and decode(JH_SBTHCD,#{sbthcd},'未上报',decode(SBZT,0,'未上报',1,'已上报'))=#{sbzt}
		
		
			and decode(SPZT,0,'未审核',1,'已审核')=#{shzt}
		
		group by sfylrbwqk
