mapper.select:
select * from (
	select rownum num,t.* from (
		select c.*,l.ghlxbh from cbsj_lmgz c left join lxsh_lmgz l on c.xmbm=l.xmbm
		where 1=1
		
			and ${cbsj.xzqhdm}
		
		
			and l.ghlxbh like '%'||#{cbsj.ghlxbh}||'%'
		
		
			and l.xjsdj = #{cbsj.xjsdj}
		
		
			and c.jsjsdj=#{cbsj.jsjsdj}
		
		
			and l.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{cbsj.tsdq})
		
		
			and c.sbzt=${cbsj.sbzt}
		
		
			and c.shzt=${cbsj.shzt}
		
		order by c.shzt,c.sbzt ) t 
) where  num>(${page}-1)*${rows} and num<=${page}*${rows}

