and ${xmbm}
and j.xmmc like '%'||#{xmmc}||'%'
and j.xdzt=${xdzt}
and ${jsdj}
and l.lsjl=#{lsjl}

	select count(*) sl,nvl(sum(yqdbcgs),0) cgs,nvl(sum(sysbbzj),0) sbz,nvl(sum(jhlc),0) lc,nvl(sum(ztz),0) tz,'0' sjl,nvl((nvl(sum(ztz),0)-nvl(sum(yqdbcgs),0)-nvl(sum(sysbbzj),0)),0) dftz from (
			select j.*,lx.lc jhlc,sl.xmsl xmsl,lxg.ntz tz,lxg.jsdj,cb.sjpfwh from jhxdyh j  left join (select xmbm,sjpfwh from cbsj_yhdzx) cb on j.xmbm=cb.xmbm  left join xmsq_yhdzx lxg on j.xmbm=lxg.xmbm left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx group by lx.xmid) l on j.xmbm=l.xmbm
            left join (select  xmid,jsxz,
            (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
            when jsxz like '%新建%' then xjlc
            else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc
            from lxsh_lx where jdbs='2' and sffirst='1' ) lx  
            on j.xmbm=lx.xmid
            left join (select xmid,count(xmid) xmsl from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid
            left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj,
            replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj,
            replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj,
            replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj
            from (select  xmid,jsxz,
            (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
            when jsxz like '%新建%' then xjlc
            else lc
            end) jhlc,lxbm,lxmc,(case when jsxz like '%新建%' then xjlxbm 
            else ''
            end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj
            from LXSH_LX  where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid
              where 1=1
			and ${xzqhdm}
			
				and ghlxbmtj like '%'||#{ghlxbh}||'%'
			
			
				and ghxlxbmtj like '%'||#{ghxlxbm}||'%'
			
			
				and ghlxmctj like '%'||#{lxmc}||'%'
			
			
				and ghxlxmctj like '%'||#{ghxlxmc}||'%'
			
			
				and substr(j.xmbm,0,4) in (${xmnf})
			
			
				${tsdq}
			
			
				and ${xzdj}
			
			and j.xdzt=${shzt}
			and j.sbzt=${sbzt}
			and j.xmmc like '%'||#{xmmc}||'%'
			and ${jsdj}
			and j.lsjl=#{lsjl}
			${xmlx1}
			${jhxdwh}
			
				${zjly}
			
			
				
			
			
				and sfsycgs = #{sfsycgs}
			
			order by j.xdzt,j.xmbm desc
		) t

