and ghxlxbmtj like '%'||#{ghxlxbm}||'%'
and ghlxmctj like '%'||#{lxmc}||'%'
and ghxlxmctj like '%'||#{ghxlxmc}||'%'
and j.xdzt=${shzt} and j.sbzt=${sbzt} and j.lsjl=#{lsjl} ${jhxdwh} order by j.xdzt,j.xmbm desc ) t

