			 left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx group by lx.xmid) l on j.xmbm=l.xmbm 
			 left join (select xmid,jsxz, (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0)) when jsxz like '%新建%'
			 then xjlc else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc from lxsh_lx where jdbs='2' and sffirst='1' ) lx on j.xmbm=lx.xmid 
			 left join (select xmid,count(xmid) xmsl from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') 
			 
			 ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj, replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj, replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj, replace(WMSYS.WM_CONCAT(lxmc),',',',') 
			 ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj from (select xmid,jsxz, (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+
			 nvl(jhwllc,0)) when jsxz like '%新建%' then xjlc else lc end) jhlc,lxbm,lxmc,(case when jsxz like '%新建%' then xjlxbm else '' end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj from LXSH_LX where jdbs='2' ) group by xmid) lx2 
			 on j.xmbm=lx2.xmid 
			 right join (select * from LXSH_LX where jdbs='2' ) l_ on j.xmbm=l_.xmid 
             where 1=1
			and ${jhsh.xzqhdm}
			and j.xmmc like '%'||#{jhsh.xmmc}||'%'
			
				and substr(j.xmbm,0,4) in (${jhsh.xmnf})
			
			and ${jhsh.jsdj}
			and ${jhsh.jsdj}
			
			
				and j.ghlxbh like '%'||#{jhsh.ghlxbh}||'%'
			
			
				and l.ghlxbm like '%'||#{jhsh.ghxlxbm}||'%'
			
			
				and l.lxmc like '%'||#{jhsh.lxmc}||'%'
			
			
				and l.ghlxmc like '%'||#{jhsh.ghxlxmc}||'%'
			
			
				${jhsh.tsdq}
			
			
				and ${jhsh.xzdj}
			
			and zydpx=#{jhsh.zydpx}
			and j.lsjl=#{jhsh.lsjl}
			${jhsh.xmlx1}
			${jhsh.jhxdwh}
			and j.sbzt=#{jhsh.sbzt}
			and j.xdzt=#{jhsh.shzt}
			
				${jhsh.zjly}
			
			
				
			
			order by j.xdzt,j.xmbm desc,l_.sffirst desc
		 
	
		where  num>(${page}-1)*${rows} and num<=${page}*${rows}

		select count(*) from (
				select j.*,lx.lc,sl.xmsl xmsl,lxg.tz,lxg.jsjsdj,lxg.xjsdj from gsdgz j  left join lxgsdgz lxg on j.xmbm=lxg.xmbm left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx group by lx.xmid) l on j.xmbm=l.xmbm
		        left join (select  xmid,jsxz,
		        (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
	            when jsxz like '%新建%' then xjlc
	            else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc
	            from lxsh_lx where jdbs='2' and sffirst='1' ) lx 
		        on j.xmbm=lx.xmid
		        left join (select xmid,count(xmid) xmsl from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid
		        left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj,
		        replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj,
		        replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj,
		        replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj
		        from (select  xmid,jsxz,
		        (case when jsxz like '%改建%' then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
	            when jsxz like '%新建%' then xjlc
	            else lc
		        end) jhlc,lxbm,lxmc,(case when jsxz like '%新建%' then xjlxbm 
		        else ''
		        end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj
		        from LXSH_LX  where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid
	            where 1=1
				and ${jhsh.xzqhdm}
				and j.xmmc like '%'||#{jhsh.xmmc}||'%'
				
					and substr(xmbm,0,4) in (${jhsh.xmnf})
				
				and ${jhsh.jsdj}
				and ${jhsh.jsdj}
				
				
					and ghlxbmtj like '%'||#{jhsh.ghlxbh}||'%'
				
				
					and ghxlxbmtj like '%'||#{jhsh.ghxlxbm}||'%'
				
				
					and ghlxmctj like '%'||#{jhsh.lxmc}||'%'
				
				
					and ghxlxmctj like '%'||#{jhsh.ghxlxmc}||'%'
				
				
					${jhsh.tsdq}
				
				
					and ${jhsh.xzdj}
				
				and zydpx=#{jhsh.zydpx}
				and j.lsjl=#{jhsh.lsjl}
				${jhsh.xmlx1}
				${jhsh.jhxdwh}
				and j.sbzt=#{jhsh.sbzt}
				and j.xdzt=#{jhsh.shzt}
				
					${jhsh.zjly}
				
				
					
				
				order by j.xdzt,j.xmbm desc
			) t

