				and ${xmsq.xmbm}
				and ${xmsq.gcfl}
				${xmsq.tsdq}
				and x.lsjl=#{xmsq.lsjl}
				and x.wnxmk=#{xmsq.wnxmk}

				
					and x.xmbm in (select xmid from lxsh_lx lx where ${xmsq.ylxbh} and xmid like '%4___')
				
				
				
