					)
				
				
				
					and X.lxmc like '%'||#{xmsq.lxmc}||'%'
				
				order by x.sqzt desc,x.xmbm desc) t ) t2
		
			where  num>(${page}-1)*${rows} and num<=${page}*${rows}

