(
select distinct min(substr(xmid,0,4)) from lxsh_lx l where x.ylxbh=l.lxbm and to_number(x.qdzh)<to_number(l.zdzh) and to_number(x.zdzh)>to_number(l.qdzh)
) v_18,'' v_19,'' v_20,x.lmkd v_21,x.bz v_22,(
select distinct substr(min(xmid),0,4)||'年'||decode(substr(min(xmid),11,1),'1','改建','2','路面改造','3','新建','4','养护大中修','5','灾毁恢复')||'('||min(to_number(l.qdzh))||'-'||max(to_number(l.zdzh))||')' from lxsh_lx l where x.ylxbh=l.lxbm and to_number(x.qdzh)<to_number(l.zdzh) and to_number(x.zdzh)>to_number(l.qdzh)
) v_23
from xmsq_yhdzx x left join 
(
select xmid,nvl(sum(l.yilc),0)+nvl(sum(l.erlc),0)+nvl(sum(l.sanlc),0)+nvl(sum(l.silc),0)+nvl(sum(l.dwlc),0)+nvl(sum(l.wllc),0) lc,sum(l.sbzj) sbzj 

from lxsh_lx l where jdbs='1' group by l.xmid
) lx on x.xmbm=lx.xmid
left join (select sjpfwh,xmbm from cbsj_yhdzx ) c on x.xmbm=c.xmbm left join (select xmid,min(lxbm) ylxbh,WMSYS.WM_CONCAT(distinct to_char(lxmc)) lxmc,min(qdzh) qdzh,min(zdzh) zdzh,min(qdmc) qdmc,min(zdmc) zdmc,
			min(lc) lc,min(yilc),min(erlc),min(sanlc),min(silc),min(dwlc),min(wllc),min(sffirst),min(ghlxbm),min(ghlxmc) from lxsh_lx where jdbs='1' group by xmid) l on x.xmbm=l.xmid
			and ${gydwdm}
			and ${xzqhdm}
			and xmmc like '%'||#{xmmc}||'%'
			and ${jsdj}
			and sqzt=${sqzt}
			and ${xmbm}
			${tsdq}
			and x.lsjl=#{lsjl}
			
				and x.xmbm in (select xmid from lxsh_lx lx where ${ylxbh} and xmid like '%4___')
			
			
				and x.wnxmk like '%'||#{wnxmk}||'%' 
			
			
				and x.ghlxbh like '%'||#{ghlxbh}||'%' 
			
			
				and l.lxmc like '%'||#{lxmc}||'%' 
			
			
				and (x.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxbm like '%'||#{ghlxbm}||'%')
				or x.xmbm in (select distinct xmid from lx_clsd where ghlxbm like '%'||#{ghlxbm}||'%')
				)
			
			
				and (x.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxmc like '%'||#{ghlxmc}||'%')
				or x.xmbm in (select distinct xmid from lx_clsd where ghlxmc like '%'||#{ghlxmc}||'%')
				)
			
	order by x.xzqh,x.gcfl,substr(x.ghlxbh,0,1)
) tb
