mapper.select:
select * from (
	select rownum num,t.* from (
		
		select x.xmbm,x.xmmc,x.xzqh,x.gydw,x.lmkd,x.jsdj,x.ntz,x.jhkgsj,x.jhwgsj,x.gq,x.gcfl,x.jsfa,x.ghlxbh,x.xzqhdm,l.*
		from xmsq_yhdzx x,(select xmid,lxbm ylxbh,lxmc lxmc,qdzh qdzh,zdzh zdzh,qdmc qdmc,zdmc zdmc,
		lc lc,yilc,erlc,sanlc,silc,dwlc,wllc from lxsh_lx where jdbs='1' and sffirst='1') l where 1=1 and x.xmbm=l.xmid
		and ${gydwdm}
		and ${xzqhdm}
		and xmmc like '%'||#{xmmc}||'%'
		and ${jsdj}
		and sqzt=${sqzt}
		and ${xmbm}
		and x.tsdq like '%'||#{tsdq}||'%'
		and x.lsjl=#{lsjl}
		
			and x.xmbm in (select xmid from lxsh_lx lx where ${ylxbh} and xmid like '%4___')
		
		order by x.sqzt desc,x.xmbm desc) t ) t2

select * from (
	select rownum num,t.* from (
		select x.xmbm,x.xmmc,x.xzqh,x.ghlxbh,x.xzqhdm,x.gydw,x.lmkd,x.jsdj,x.ntz,x.jhkgsj,x.jhwgsj,x.gq,x.gcfl,x.jsfa,l.*
        from xmsq_sh x,(select xmid,lxbm ylxbh,lxmc lxmc,qdzh qdzh,zdzh zdzh,qdmc qdmc,zdmc zdmc,
        lc lc,yilc,erlc,sanlc,silc,dwlc,wllc from lxsh_lx where jdbs='1' and sffirst='1') l where 1=1 and x.xmbm=l.xmid
		and ${gydwdm}
		and ${xzqhdm}
		and xmmc like '%'||#{xmmc}||'%'
		and (jsdj=#{jsdj})
		and sqzt=${sqzt}
		and ${xmbm}
		and x.tsdq like '%'||#{tsdq}||'%'
		and x.lsjl=#{lsjl}
		
			and x.xmbm in (select xmid from lxsh_lx lx where ${ylxbh} and xmid like '%5___')
		
		order by x.sqzt desc,x.xmbm desc) t ) t2

select count(*) bz,sum(ntz) ntz,sum(lc) lc from (
	select xmbm,ntz,x.lc from xmsq_yhdzx x left join 
	(select xmid,sum(lc) lc from lxsh_lx where jdbs=#{jdbs} and xmid like '%4___' group by xmid) l on x.xmbm=l.xmid where 1=1
	and ${gydwdm}
	and ${xzqhdm}
	and xmmc like '%'||#{xmmc}||'%'
	and ${jsdj}
	and sqzt=${sqzt}
	and ${xmbm}
	and ${gcfl}
	and x.tsdq like '%'||#{tsdq}||'%'
	and x.lsjl=#{lsjl}
	
		and x.xmbm in (select xmid from lxsh_lx lx where ${ylxbh} and xmid like '%4___')
	
	
			and ghlxbh like '%'||#{ghlxbh}||'%'
		
		
			and lxmc like '%'||#{lxmc}||'%'
		
)

select count(*) bz,sum(ntz) ntz,sum(lc) lc from (
	select xmbm,ntz,x.lc from xmsq_sh x left join 
	(select xmid,sum(lc) lc from lxsh_lx where jdbs=#{jdbs} and xmid like '%5___' group by xmid) l 
	on x.xmbm=l.xmid where 1=1
	and ${gydwdm}
	and ${xzqhdm}
	and xmmc like '%'||#{xmmc}||'%'
	and ${jsdj}
	and sqzt=${sqzt}
	and ${xmbm}
	and ${gcfl}
	and x.tsdq like '%'||#{tsdq}||'%'
	and x.lsjl=#{lsjl}
	
		and x.xmbm in (select xmid from lxsh_lx lx where ${ylxbh} and xmid like '%5___')
	
	
			and ghlxbh like '%'||#{ghlxbh}||'%'
		
		
			and lxmc like '%'||#{lxmc}||'%'
		
)
