mapper.select:
		select * from (
		select rownum num ,t.*,t1.lsjl,t1.ghlxbh lxbh,t1.gydwdm,tt.lc, t.shzt sbzt1  from KXXYJ_SJGZ t Left join lxsh_sjgz t1 on t.xmbm=t1.xmbm left join (select sum(lc) lc,xmid xmbm from lxsh_lx where jdbs='1' group by xmid) tt on t.xmbm=tt.xmbm  where 1=1
			and (t.sbthcd <=7 or t.sbthcd is null) 
		
			${gydw}
		
		
			${xzqh}
		
		
			and t.xmmc like '%'||#{xmmc}||'%'
		
		
			and t1.xmnf like '%'||#{xmnf}||'%'
		



		
			and t1.tsdq like '%'||#{tsdq}||'%'
		
		
			and t.jsjsdj like '%'||#{jsdj}||'%'
		
		
			and ghlxbh like #{gldj}||'%'
		 
		
			and t1.lsjl like '%'||#{lsjl}||'%'
		
		) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
		order by sbzt1,xmbm

		select * from (
		select rownum num ,t.*,t1.lsjl,t1.ghlxbh lxbh,t1.gydwdm,tt.lc,t.shzt sbzt1  from KXXYJ_LMGZ t Left join lxsh_lmgz t1 on t.xmbm=t1.xmbm left join (select sum(lc) lc,xmid xmbm from lxsh_lx where jdbs='1' group by xmid) tt on t.xmbm=tt.xmbm where 1=1
			and (t.sbthcd <=7 or t.sbthcd is null) 
		
			${gydw}
		
		
			${xzqh}
		
		
			and t.xmmc like '%'||#{xmmc}||'%'
		
		
			and t1.xmnf like '%'||#{xmnf}||'%'
		



		
			and t1.tsdq like '%'||#{tsdq}||'%'
		
		
			and t.jsjsdj like '%'||#{jsdj}||'%'
		
		
			and ghlxbh like #{gldj}||'%'
		
		
			and t1.lsjl like '%'||#{lsjl}||'%'
		
		) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
		order by sbzt1,xmbm

		select * from (
		select rownum num ,t.*,t1.ghlxbh lxbh,t1.gydwdm,tt.lc,t.shzt sbzt1  from KXXYJ_XJ t Left join lxsh_xj t1 on t.xmbm=t1.xmbm left join (select sum(lc) lc,xmid xmbm from lxsh_lx where jdbs='1' group by xmid) tt on t.xmbm=tt.xmbm where 1=1
			and (t.sbthcd <=7 or t.sbthcd is null) 
		
			${gydw}
		
		
			${xzqh}
		
		
			and t.xmmc like '%'||#{xmmc}||'%'
		
		
			and t1.xmnf like '%'||#{xmnf}||'%'
		



		
			and t1.tsdq like '%'||#{tsdq}||'%'
		
		
			and t.jsjsdj like '%'||#{jsdj}||'%'
		
		
			and ghlxbh like #{gldj}||'%'
		
		) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
		order by sbzt1,xmbm

select * from (
select rownum num ,t.*,t1.ghlxbh lxbh,t1.gydwdm  from KXXYJ_SJGZ t Left join lxsh_sjgz t1 on t.xmbm=t1.xmbm where 1=1
	and  t.sbthcd=7

	${gydw}


	${xzqh}


	and t.xmmc like '%'||#{xmmc}||'%'


	and t1.xmnf like '%'||#{xmnf}||'%'


	and t.shzt like '%'||#{sbzt1}||'%'


	and t1.tsdq like '%'||#{tsdq}||'%'


	and t.jsjsdj like '%'||#{jsdj}||'%'


	and ghlxbh like #{gldj}||'%'

) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
order by xmbm

