mapper.select:
select * from ( select rownum num ,t.*,t1.lsjl,t1.ghlxbh lxbh,t1.gydwdm, t.shzt sbzt1,lx.* from KXXYJ_SJGZ t Left join lxsh_sjgz t1 on t.xmbm=t1.xmbm left join (select qdmc,zdmc,min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,sum(lc) lc,yilc,erlc,sanlc,silc,dwlc,wllc,jhyilc,jherlc,jhsanlc,jhsilc,jhdwlc,jhwllc from lxsh_lx where jdbs='1' group by qdmc,zdmc,xmid,yilc,erlc,sanlc,silc,dwlc,wllc,jhyilc,jherlc,jhsanlc,jhsilc,jhdwlc,jhwllc) lx on t.xmbm=lx.xmid where 1=1 and (t.sbthcd <=7 or t.sbthcd is null)
${gydw}
${xzqh}
and t.xmmc like '%'||#{xmmc}||'%'
and t1.xmnf like '%'||#{xmnf}||'%'
and t1.tsdq like '%'||#{tsdq}||'%'
and t.jsjsdj like '%'||#{jsdj}||'%'
and ghlxbh like #{gldj}||'%'
and t1.lsjl like '%'||#{lsjl}||'%'
) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows} order by sbzt1,xmbm

select * from ( select rownum num ,t.*,t1.lsjl,t1.ghlxbh lxbh,t1.gydwdm,t.shzt sbzt1,lx.* from KXXYJ_LMGZ t Left join lxsh_lmgz t1 on t.xmbm=t1.xmbm left join (select qdmc,zdmc,min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,sum(lc) lc,yilc,erlc,sanlc,silc,dwlc,wllc from lxsh_lx where jdbs='1' group by qdmc,zdmc,xmid,yilc,erlc,sanlc,silc,dwlc,wllc) lx on t.xmbm=lx.xmid where 1=1 and (t.sbthcd <=7 or t.sbthcd is null)
${gydw}
${xzqh}
and t.xmmc like '%'||#{xmmc}||'%'
and t1.xmnf like '%'||#{xmnf}||'%'
and t1.tsdq like '%'||#{tsdq}||'%'
and t.jsjsdj like '%'||#{jsdj}||'%'
and ghlxbh like #{gldj}||'%'
and t1.lsjl like '%'||#{lsjl}||'%'
) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows} order by sbzt1,xmbm

select * from ( select rownum num ,t.*,t1.ghlxbh lxbh,t1.gydwdm,t.shzt sbzt1,lx.* from KXXYJ_XJ t Left join lxsh_xj t1 on t.xmbm=t1.xmbm left join (select qdmc,zdmc,lxmc,min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,sum(lc) lc,sum(xjlc) xjlc,yilc,erlc,sanlc,silc,dwlc,wllc,xjlxbm,xjqdzh,xjzdzh from lxsh_lx where jdbs='1' group by qdmc,zdmc,lxmc,xmid,yilc,erlc,sanlc,silc,dwlc,wllc,xjlxbm,xjqdzh,xjzdzh,xjlc ) lx on t.xmbm=lx.xmid where 1=1 and (t.sbthcd <=7 or t.sbthcd is null)
${gydw}
${xzqh}
and t.xmmc like '%'||#{xmmc}||'%'
and t1.xmnf like '%'||#{xmnf}||'%'
and t1.tsdq like '%'||#{tsdq}||'%'
and t.jsjsdj like '%'||#{jsdj}||'%'
and ghlxbh like #{gldj}||'%'
) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows} order by sbzt1,xmbm

