mapper.select:
select * from (
select rownum num ,t.*,decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) sbzt1  from LXSH_SJGZ t where 1=1
	and  sbthcd <=#{sbthcd}

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and xmnf like '%'||#{xmnf}||'%'


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and xjsdj like '%'||#{xjsdj}||'%'


	and ghlxbm like '#{tsdq}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}
order by xmbm

select count(*) from LXSH_SJGZ where 1=1
and  sbthcd <=#{sbthcd}

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and xmnf like '%'||#{xmnf}||'%'


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and xjsdj like '%'||#{xjsdj}||'%'


	and ghlxbm like '#{tsdq}||'%'

select * from (
select rownum num ,t.id,xmid xmbm,lxbm ghlxbh,lxmc,xzqh,zdzh,qdzh,gydw,xzqhdm,gydwdm,jsjsdj,xjsdj,lc,jsxz,qdmc,zdmc  from LXSH_LX t where 1=1 and sffirst!='1'

	and xmid like '%'||#{xmbm}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}

select * from (
select rownum num ,t.*  from LXSH_SJGZ t where 1=1
	and  sbthcd=7

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and xmnf like '%'||#{xmnf}||'%'


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and xjsdj like '%'||#{xjsdj}||'%'


	and ghlxbm like '#{tsdq}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}
order by xmbm
