select * from (
select rownum num ,t.*,decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) sbzt1,lx.*  from LXSH_SJGZ t left join
(select min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,yilc,erlc,sanlc,silc,dwlc,wllc,jhyilc,jherlc,jhsanlc,jhsilc,jhdwlc,jhwllc 
from lxsh_lx where jdbs='0' group by xmid,yilc,erlc,sanlc,silc,dwlc,wllc,jhyilc,jherlc,jhsanlc,jhsilc,jhdwlc,jhwllc) lx on t.xmbm=lx.xmid where 1=1
	and  sbthcd <=#{sbthcd}

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and xmnf like '%'||#{xmnf}||'%'


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and xjsdj like '%'||#{jsdj}||'%'


	and ghlxbh like #{gldj}||'%'


	and lsjl like '%'||#{lsjl}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}
order by sbzt1,xmbm

select count(*) from LXSH_SJGZ where 1=1 and sbthcd <=#{sbthcd}
${gydw}
${xzqh}
and xmmc like '%'||#{xmmc}||'%'
and xmnf like '%'||#{xmnf}||'%'
and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'
and tsdq like '%'||#{tsdq}||'%'
and xjsdj like '%'||#{jsdj}||'%'
and ghlxbh like #{gldj}||'%'
and lsjl like '%'||#{lsjl}||'%'

