(select min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,yilc,erlc,sanlc,silc,dwlc,wllc,xjlxbm,xjqdzh,xjzdzh,xjlc 
from lxsh_lx where jdbs='0' group by xmid,yilc,erlc,sanlc,silc,dwlc,wllc,xjlxbm,xjqdzh,xjzdzh,xjlc) lx1 on t.xmbm=lx1.xmid
where 1=1
	and  sbthcd <=#{sbthcd}

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and xmnf like '%'||#{xmnf}||'%'


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and xjsdj like '%'||#{jsdj}||'%'


	and ghlxbh like #{gldj}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}
order by sbzt1,xmbm

