select * from (
select rownum num ,t.*,decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) sbzt1,lx.*  from LXSH_SJGZ t left join
(select min(qdzh) minqdzh,max(zdzh) maxzdzh,xmid,sum(yilc) yilc,sum(erlc) erlc,sum(sanlc) sanlc,sum(silc) silc,
sum(dwlc) dwlc,sum(wllc) wllc,sum(jhyilc) jhyilc,sum(jherlc) jherlc,sum(jhsanlc) jhsanlc,sum(jhsilc) jhsilc,sum(jhdwlc) jhdwlc,sum(jhwllc) jhwllc 
from lxsh_lx where jdbs='0' group by xmid) lx on t.xmbm=lx.xmid where 1=1
	and  sbthcd <=#{sbthcd}

	${gydw}


	${xzqh}


	and xmmc like '%'||#{xmmc}||'%'


	and ${xmnf}


	and decode(sbthcd,#{sbthcd},'0',to_char(sbzt)) like '%'||#{sbzt1}||'%'


	and tsdq like '%'||#{tsdq}||'%'


	and ${jsdj}


	and ghlxbh like #{gldj}||'%'


	and lsjl like '%'||#{lsjl}||'%'

) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows}
order by sbzt1,xmbm

