mapper.update:
update xtgl_master set truename=#{truename}

	 ,name =#{name}


	 ,password=#{password} 


	 ,sex=#{sex} 


	 ,tel=#{tel} 


	 ,phone=#{phone} 


	 ,idcard=#{idcard} 


	 ,role=#{role} 


	 ,unit=#{unit} 

where id=#{id}

mapper.select:
select roleid,rolename,desr,(select WMSYS.WM_CONCAT(to_char(sourceid)) from xtgl_role_resource where roleid=t.roleid) sourceid
from xtgl_role t
where roleid=#{roleid}

select sourceid id,name,parent from xtgl_role_resource a,xtgl_resource b  where a.roleid=#{roleid}
and a.sourceid=b.id
order by b.id,parent
